--

CREATE TABLE `tbl_tintuc` (
  `ID` smallint(6) NOT NULL,
  `TieuDe` varchar(250) COLLATE utf8mb4_unicode_ci NOT NULL,
  `NoiDung` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `HinhDaiDien` varchar(250) COLLATE utf8mb4_unicode_ci NOT NULL,
  `NgayDang` date NOT NULL,
  `NguoiDang` varchar(250) COLLATE utf8mb4_unicode_ci NOT NULL,
  `IDLoaiTinTuc` int(11) NOT NULL,
  `viewcount` int(11) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Đang đổ dữ liệu cho bảng `tbl_tintuc`
--

INSERT INTO `tbl_tintuc` (`ID`, `TieuDe`, `NoiDung`, `HinhDaiDien`, `NgayDang`, `NguoiDang`, `IDLoaiTinTuc`, `viewcount`) VALUES
(1, 'Chelsea Trình Làng Ba Mẫu Áo Bóng Đá Cực Chất Cho Mùa Giải 2019/2023\r\n', '<p><strong>Chelsea</strong>&nbsp;được biết đến l&agrave; c&acirc;u lạc bộ b&oacute;ng đ&aacute; đến từ Anh Quốc đ&acirc;y l&agrave; một trong những c&acirc;u lạc bộ đứng đầu thế giới v&agrave; c&oacute; lượng cổ động vi&ecirc;n h&ugrave;ng hậu. Khởi đầu 2019 Chelsea tr&igrave;nh l&agrave;ng giới thiệu đến người h&acirc;m mộ những mẫu &aacute;o đấu được xem l&agrave; chiến phục cho m&ugrave;a giải năm nay. M&ugrave;a h&egrave; 2018 c&aacute;c đội b&oacute;ng h&agrave;ng đầu bắt đầu rục rịch cho ra mắt những mẫu &aacute;o đấu mang thương hiệu ri&ecirc;ng của m&igrave;nh.&nbsp;<strong>Chelsea</strong>&nbsp;cũng kh&ocirc;ng ngoại lệ l&agrave; đội b&oacute;ng Top đầu kh&ocirc;ng qu&aacute; kh&oacute; hiểu khi trang phục thi đấu của đội b&oacute;ng lu&ocirc;n l&agrave; t&acirc;m điểm ch&uacute; &yacute; kh&ocirc;ng chỉ của cổ động vi&ecirc;n fan h&acirc;m mộ m&agrave; n&oacute; c&ograve;n l&agrave; cuộc cạnh tranh của c&aacute;c nh&agrave; t&agrave;i trợ trang phục cho đội b&oacute;ng. Việc đặt logo thương hiệu của m&igrave;nh l&ecirc;n chiếc &aacute;o đấu Chelsea chưa bao giờ l&agrave; dễ d&agrave;ng c&ugrave;ng kh&aacute;m ph&aacute; ba mẫu &aacute;o đấu Chelsea 2019/2020 chất ph&aacute;t ngất do Nike thiết kế ngay h&ocirc;m nay.</p>\r\n\r\n<h2>1/ &Aacute;o b&oacute;ng đ&aacute; Chelsea s&acirc;n nh&agrave; 2019/2020</h2>\r\n\r\n<p>Nếu l&agrave; fan h&acirc;m mộ của&nbsp;<strong>c&acirc;u lạc bộ Chelsea</strong>&nbsp;bạn ắt hẳn phải biết đến biệt danh&nbsp;<strong>The Blues</strong>. Gắn liền với sự ph&aacute;t triển cũng như th&agrave;nh c&ocirc;ng của c&acirc;u lạc bộ xanh dương lu&ocirc;n được mệnh danh l&agrave; m&agrave;u sắc của Chelsea. Ch&iacute;nh do yếu tố n&agrave;y n&ecirc;n Nike kh&ocirc;ng thể bỏ qua m&agrave;u xanh cho chiếc &aacute;o đấu của Chelsea. Bộ &aacute;o s&acirc;n nh&agrave; mới 2019-2020, được lấy cảm hứng rất nhiều từ Stamford Bridge, s&acirc;n nh&agrave; của c&acirc;u lạc bộ kể từ năm 1905. &Aacute;o b&oacute;ng đ&aacute; Chelsea 2019 2020 sẽ được ra mắt trong trận đấu cuối c&ugrave;ng của nh&agrave; M&ugrave;a giải M&ugrave;a giải Premier League, gặp Watford v&agrave;o ng&agrave;y 5 th&aacute;ng Năm.</p>\r\n\r\n<p><a href=\"https://dothethao.net.vn/san-pham/ao-bong-da-chelsea-san-nha-2019-2020-mau-xanh/\"><img alt=\"Áo bóng đá Chelsea 2019 2020 màu xanh\" sizes=\"(max-width: 800px) 100vw, 800px\" src=\"https://dothethao.net.vn/wp-content/uploads/2019/02/ao-dau-chelsea-2019-2020-3.jpg\" srcset=\"https://hongphucsport.com/wp-content/uploads/2019/02/ao-dau-chelsea-2019-2020-3.jpg 800w, https://hongphucsport.com/wp-content/uploads/2019/02/ao-dau-chelsea-2019-2020-3-510x765.jpg 510w, https://hongphucsport.com/wp-content/uploads/2019/02/ao-dau-chelsea-2019-2020-3-100x150.jpg 100w, https://hongphucsport.com/wp-content/uploads/2019/02/ao-dau-chelsea-2019-2020-3-200x300.jpg 200w, https://hongphucsport.com/wp-content/uploads/2019/02/ao-dau-chelsea-2019-2020-3-768x1152.jpg 768w, https://hongphucsport.com/wp-content/uploads/2019/02/ao-dau-chelsea-2019-2020-3-683x1024.jpg 683w\" /></a></p>\r\n\r\n<p>Cũng giống như hai m&ugrave;a trước,&nbsp;<strong>&aacute;o s&acirc;n nh&agrave; của Chelsea 2019</strong>&nbsp;-2020 chủ yếu l&agrave; &lsquo;m&agrave;u xanh nước biển&rsquo;. Họa tiết h&igrave;nh họa, tối hơn một ch&uacute;t được lấy cảm hứng từ Stamford Bridge.</p>\r\n\r\n<p>Họa tiết tr&ecirc;n &aacute;o l&agrave; h&igrave;nh ảnh hợp nhất của North Stand, East and West Stands v&agrave; Shed End th&agrave;nh một bố cục trừu trượng, độc đ&aacute;o. Phẩn cổ b&ecirc;n trong &aacute;o sơ mi 2019-2020 của Chelsea thấy được một con sư tử với d&ograve;ng chữ Pride of London Shaw được viết xung quanh n&oacute;. &Aacute;o b&oacute;ng đ&aacute; thi đấu clb chelsea được bổ sung một bảng m&agrave;u đỏ ph&iacute;a sau cổ với d&ograve;ng chữ &ldquo;CFC&rdquo;</p>\r\n\r\n<p><a href=\"https://dothethao.net.vn/san-pham/ao-bong-da-chelsea-san-nha-2019-2020-mau-xanh/\"><img src=\"https://dothethao.net.vn/wp-content/uploads/2019/02/ao-dau-chelsea-2019-2020-4.jpg\" srcset=\"https://hongphucsport.com/wp-content/uploads/2019/02/ao-dau-chelsea-2019-2020-4.jpg 800w, https://hongphucsport.com/wp-content/uploads/2019/02/ao-dau-chelsea-2019-2020-4-510x765.jpg 510w, https://hongphucsport.com/wp-content/uploads/2019/02/ao-dau-chelsea-2019-2020-4-100x150.jpg 100w, https://hongphucsport.com/wp-content/uploads/2019/02/ao-dau-chelsea-2019-2020-4-200x300.jpg 200w, https://hongphucsport.com/wp-content/uploads/2019/02/ao-dau-chelsea-2019-2020-4-768x1152.jpg 768w, https://hongphucsport.com/wp-content/uploads/2019/02/ao-dau-chelsea-2019-2020-4-683x1024.jpg 683w\" /></a></p>\r\n\r\n<p>Bộ đồng phục thi đấu năm nay của Chelsea rất được l&ograve;ng giới h&acirc;m mộ CLB. Nếu bạn l&agrave; một fan nhiệt th&agrave;nh của CLB Chelsea vậy c&ograve;n chần chờ g&igrave; nữa để sở hữu 1 bộ c&aacute;nh cho ri&ecirc;ng m&igrave;nh. Xem chi tiết mẫu &aacute;o b&oacute;ng đ&aacute; s&acirc;n nh&agrave; Chelsea tại&nbsp;<a href=\"https://dothethao.net.vn/san-pham/ao-bong-da-chelsea-san-nha-2019-2020-mau-xanh/\">Đ&Acirc;Y</a>.</p>\r\n\r\n<h2>2/ &Aacute;o b&oacute;ng đ&aacute; Chelsea s&acirc;n kh&aacute;ch 2019/2020</h2>\r\n\r\n<p>Mẫu &aacute;o b&oacute;ng đ&aacute; Chelsea s&acirc;n kh&aacute;ch 2019 2020 của Chelsea FC giới thiệu một c&aacute;i nh&igrave;n rất mới mẻ với &aacute;o truyền thống cổ polo, kết hợp ch&acirc;n đế m&agrave;u trắng với viền m&agrave;u xanh đỏ. Được sản xuất bởi Nike, &aacute;o b&oacute;ng đ&aacute; s&acirc;n kh&aacute;ch Chelsea 2019 2020 c&oacute; sự g&oacute;p mặt của Yokohama Tyres.</p>\r\n\r\n<p><img src=\"https://dothethao.net.vn/wp-content/uploads/2019/02/ao-dau-chelsea-2019-2020-6.jpg\" srcset=\"https://hongphucsport.com/wp-content/uploads/2019/02/ao-dau-chelsea-2019-2020-6.jpg 1200w, https://hongphucsport.com/wp-content/uploads/2019/02/ao-dau-chelsea-2019-2020-6-510x287.jpg 510w, https://hongphucsport.com/wp-content/uploads/2019/02/ao-dau-chelsea-2019-2020-6-150x84.jpg 150w, https://hongphucsport.com/wp-content/uploads/2019/02/ao-dau-chelsea-2019-2020-6-300x169.jpg 300w, https://hongphucsport.com/wp-content/uploads/2019/02/ao-dau-chelsea-2019-2020-6-768x432.jpg 768w, https://hongphucsport.com/wp-content/uploads/2019/02/ao-dau-chelsea-2019-2020-6-1024x576.jpg 1024w\" /></p>\r\n\r\n<p>Với phong c&aacute;ch cổ điển, &aacute;o s&acirc;n kh&aacute;ch 2019 2020 cưa Chelsea c&oacute; m&agrave;u trắng với c&aacute;c điểm nhấn m&agrave;u xanh v&agrave; đỏ. Bộ &aacute;o c&oacute; cổ polo trắng v&agrave; cổ tay &aacute;o với viền m&agrave;u đỏ v&agrave; m&agrave;u xanh mỏng.</p>\r\n\r\n<p><img src=\"https://dothethao.net.vn/wp-content/uploads/2019/02/ao-dau-chelsea-2019-2020-5.jpg\" srcset=\"https://hongphucsport.com/wp-content/uploads/2019/02/ao-dau-chelsea-2019-2020-5.jpg 806w, https://hongphucsport.com/wp-content/uploads/2019/02/ao-dau-chelsea-2019-2020-5-510x759.jpg 510w, https://hongphucsport.com/wp-content/uploads/2019/02/ao-dau-chelsea-2019-2020-5-101x150.jpg 101w, https://hongphucsport.com/wp-content/uploads/2019/02/ao-dau-chelsea-2019-2020-5-202x300.jpg 202w, https://hongphucsport.com/wp-content/uploads/2019/02/ao-dau-chelsea-2019-2020-5-768x1143.jpg 768w, https://hongphucsport.com/wp-content/uploads/2019/02/ao-dau-chelsea-2019-2020-5-688x1024.jpg 688w\" /></p>\r\n\r\n<p>Bộ &aacute;o b&oacute;ng đ&aacute; Chelsea s&acirc;n kh&aacute;ch 2019 2020 với thiết kế truyền thống được lấy cảm hứng từ &lsquo;Mods&rsquo; một n&eacute;t văn h&oacute;a nh&oacute;m ph&aacute;t triển mạnh ở London v&agrave;o những năm 1960 v&agrave; đặc biệt l&agrave; quanh đường King, c&aacute;ch Stamford Bridge kh&ocirc;ng xa.</p>\r\n\r\n<h2>3/ &Aacute;o b&oacute;ng đ&aacute; thủ m&ocirc;n Chelsea 2019/2020</h2>\r\n\r\n<p>Mới đ&acirc;y CLB Chelea đ&atilde; giới thiệu người h&acirc;m mộ bộ&nbsp;<strong>&aacute;o b&oacute;ng đ&aacute; thủ m&ocirc;n Chelsea 2019</strong>&nbsp;2020 m&agrave;u cam, cho thấy một thiết kế v&ocirc; c&ugrave;ng nổi bật v&agrave; bắt mắt. Điều đặc biệt trong lần ra mắt lần n&agrave;y tr&ecirc;n s&acirc;n nh&agrave;&nbsp; Watford, Kepa Arrizabalaga đ&atilde; mặc bộ thủ m&ocirc;n 2018 2019 m&agrave;u xanh l&aacute; c&acirc;y.</p>\r\n\r\n<p><img src=\"https://dothethao.net.vn/wp-content/uploads/2019/07/ao-bong-da-thu-mon-chelsea-2019-2020-mau-cam.jpg\" srcset=\"https://hongphucsport.com/wp-content/uploads/2019/07/ao-bong-da-thu-mon-chelsea-2019-2020-mau-cam.jpg 900w, https://hongphucsport.com/wp-content/uploads/2019/07/ao-bong-da-thu-mon-chelsea-2019-2020-mau-cam-510x680.jpg 510w, https://hongphucsport.com/wp-content/uploads/2019/07/ao-bong-da-thu-mon-chelsea-2019-2020-mau-cam-113x150.jpg 113w, https://hongphucsport.com/wp-content/uploads/2019/07/ao-bong-da-thu-mon-chelsea-2019-2020-mau-cam-225x300.jpg 225w, https://hongphucsport.com/wp-content/uploads/2019/07/ao-bong-da-thu-mon-chelsea-2019-2020-mau-cam-768x1024.jpg 768w\" /></p>\r\n\r\n<p>Dựa tr&ecirc;n&nbsp;<strong>mẫu thủ m&ocirc;n Chelsea</strong>&nbsp;Nike 2019 mới, đ&atilde; được sử dụng cho bộ sưu tập World Cup nữ 2019, &aacute;o mới của Chelsea c&oacute; m&agrave;u v&agrave;ng với logo m&agrave;u đen v&agrave; hoa văn h&igrave;nh học ở mặt trước.</p>\r\n\r\n<p>Bộ đồng phục thi đấu năm nay của Chelsea rất được l&ograve;ng giới h&acirc;m mộ CLB. Nếu bạn l&agrave; một fan nhiệt th&agrave;nh của CLB Chelsea vậy c&ograve;n chần chờ g&igrave; nữa để sở hữu 1 bộ c&aacute;nh cho ri&ecirc;ng m&igrave;nh. Xem chi tiết mẫu &aacute;o b&oacute;ng đ&aacute; s&acirc;n nh&agrave; Chelsea tại&nbsp;<a href=\"https://dothethao.net.vn/san-pham/ao-bong-da-thu-mon-chelsea-2019-2020-mau-cam/\">Đ&Acirc;Y</a>.</p>\r\n\r\n<h2>4/ Si&ecirc;u phẩm &aacute;o b&oacute;ng đ&aacute; mang t&ecirc;n Chelsea</h2>\r\n\r\n<p>Với si&ecirc;u phẩm &aacute;o đấu n&agrave;y người h&acirc;m mộ c&oacute; thể biết được tất cả những th&ocirc;ng tin li&ecirc;n quan đến đội b&oacute;ng Chelsea th&ocirc;ng qua một lần qu&eacute;t m&atilde;. Mẫu &aacute;o đấu n&agrave;y được thiết kế với đa dạng c&aacute;c phi&ecirc;n bản để c&oacute; thể đ&aacute;p ứng nhu cầu sử dụng của mọi lứa tuổi, giới t&iacute;nh khi cho ra đời phi&ecirc;n bản nữ c&oacute; gi&aacute; 64,88 bảng anh, Trẻ em được b&aacute;n với mức gi&aacute; ni&ecirc;m yết đ&oacute; l&agrave; 49,90 bảng anh. Đặc biệt hơn khi Chelsea quyết định sản xuất &aacute;o đấu cho trẻ sơ sinh với mức gi&aacute; rơi v&agrave;o khoảng 39,91 bảng anh.</p>\r\n\r\n<p>V&igrave; l&agrave; si&ecirc;u phẩm với c&ocirc;ng nghệ hiện đại kh&ocirc;ng qu&aacute; kh&oacute; hiểu khi &aacute;o đấu Chelsea limited n&agrave;y c&oacute; gi&aacute; kh&aacute; cao. Chelsea b&aacute;n gi&aacute; ni&ecirc;m yết cho &aacute;o l&agrave; 89,95 bảng, quần l&agrave; 54,95 bảng v&agrave; tất l&agrave; 24,95 bảng anh/ đ&ocirc;i.</p>\r\n\r\n<p>Với mức gi&aacute; kh&ocirc;ng hề nhỏ n&oacute; khiến cổ động vi&ecirc;n c&acirc;u lạc bộ n&agrave;y kh&aacute; hoang mang mặc d&ugrave; được trải nghiệm kh&aacute; nhiều tiện &iacute;ch. Điều n&agrave;y khiến những cổ động vi&ecirc;n Việt ch&ugrave;n ch&acirc;n mỗi khi nghĩ đến việc sở hữu cho minh chiếc &aacute;o đấu của c&acirc;u lạc bộ m&igrave;nh y&ecirc;u th&iacute;ch.</p>\r\n\r\n<p><img src=\"https://dothethao.net.vn/wp-content/uploads/2019/02/ao-dau-chelsea-2019-2020-7.jpg\" srcset=\"https://hongphucsport.com/wp-content/uploads/2019/02/ao-dau-chelsea-2019-2020-7.jpg 1200w, https://hongphucsport.com/wp-content/uploads/2019/02/ao-dau-chelsea-2019-2020-7-510x364.jpg 510w, https://hongphucsport.com/wp-content/uploads/2019/02/ao-dau-chelsea-2019-2020-7-150x107.jpg 150w, https://hongphucsport.com/wp-content/uploads/2019/02/ao-dau-chelsea-2019-2020-7-300x214.jpg 300w, https://hongphucsport.com/wp-content/uploads/2019/02/ao-dau-chelsea-2019-2020-7-768x549.jpg 768w, https://hongphucsport.com/wp-content/uploads/2019/02/ao-dau-chelsea-2019-2020-7-1024x732.jpg 1024w\" /></p>\r\n\r\n<p>Vậy c&oacute; giải ph&aacute;p n&agrave;o cho những cổ động vi&ecirc;n Chelsea Việt? &nbsp;Bạn c&oacute; bao giờ nghĩ đến việc tiết kiệm được một m&oacute;n chi ph&iacute; m&agrave; vẫn sở hữu ngay cho m&igrave;nh những mẫu &aacute;o đấu Chelsea chất lừ chưa ? Tại sao lại cần bỏ ra qu&aacute; nhiều chi ph&iacute; để sở hữu chiếc &aacute;o đấu limited trong khi n&oacute; ngo&agrave;i tầm tay của bạn</p>\r\n', '../template4.3.1/images/AnhTinTuc/anhtintuc4.jpg', '2020-10-20', 'Quản trị viên', 1, 29),
(2, 'Top Các Mẫu Áo Bóng Đá Đẹp Nhất Giải Ngoại Hạng Anh 2019 – 2020', '<p>Khởi động cho giải Ngoại Hạng năm nay c&aacute;c c&acirc;u lạc bộ tham dự lần lượt tr&igrave;nh l&agrave;ng những mẫu &aacute;o đấu cực chất của đội tuyển m&igrave;nh. Kh&ocirc;ng chỉ giới thiệu đến người h&acirc;m mộ to&agrave;n Thế giới những mẫu &aacute;o đấu ấn tượng m&agrave; c&aacute;c c&acirc;u lạc bộ c&ograve;n muốn gửi gắm v&agrave;o đ&oacute; những th&ocirc;ng điệp cũng như mục ti&ecirc;u của đội tuyển m&igrave;nh trong m&ugrave;a giải năm nay.</p>\r\n\r\n<p><img class=\"img-fluid\" src=\"https://dothethao.net.vn/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020.jpg\" srcset=\"https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020.jpg 1200w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-510x287.jpg 510w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-150x84.jpg 150w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-300x169.jpg 300w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-768x432.jpg 768w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1024x576.jpg 1024w\" /></p>\r\n\r\n<p>&Aacute;o đấu của c&aacute;c c&acirc;u lạc bộ tham dự giải ngoại hạng lu&ocirc;n được xem l&agrave; m&oacute;n đồ g&acirc;y sốt mỗi khi tr&igrave;nh l&agrave;ng. &nbsp;Ngay sau khi tập hợp được tất cả 20 mẫu &aacute;o đấu của c&aacute;c CLB tham dự giải ngoại hạng m&ugrave;a giải năm nay c&aacute;c cổ động vi&ecirc;n đ&atilde; ngay lập tức thực hiện chiến dịch bầu chọn cho mẫu &aacute;o đấu g&acirc;y ấn tượng nhất m&ugrave;a giải 2019 -2020.</p>\r\n\r\n<p>Kết quả được đưa ra khi Top ba mẫu &aacute;o đẹp nhất giải ngoại hạng năm nay xuất hiện những c&aacute;i t&ecirc;n kh&aacute; mới. Mẫu &aacute;o đấu xuất sắc gi&agrave;nh hạng nhất cho mẫu &aacute;o đấu đẹp nhất giải Ngoại hạng thuộc về chiến phục của&nbsp;<strong>Manchester United.</strong>&nbsp;Xuất sắc gi&agrave;nh vị tr&iacute; thứ hai l&agrave; c&acirc;u lạc bộ&nbsp;<strong>CLB Chelsea.&nbsp;</strong>V&agrave; cuối c&ugrave;ng hạng ba thuộc về mẫu &aacute;o đấu của&nbsp;<strong>CLB Tottenham .&nbsp;</strong>C&ugrave;ng t&igrave;m hiểu về 3 mẫu &aacute;o đấu ấn tượng n&agrave;y ngay nh&eacute;.</p>\r\n\r\n<h2>1/ &Aacute;o b&oacute;ng đ&aacute;&nbsp;<strong>Manchester United 2019/2020</strong></h2>\r\n\r\n<p>Adidas v&agrave; Manchester United vừa ra mắt bộ &aacute;o b&oacute;ng đ&aacute; thi đấu s&acirc;n nh&agrave; của c&acirc;u lạc bộ Manchester United. Thiết kế được lấy cảm hứng từ những ng&agrave;y đầu th&agrave;nh lập của c&acirc;u lạc bộ, &aacute;o b&oacute;ng đ&aacute; s&acirc;n nh&agrave; Manchester United 2019 2020 giới thiệu một thiết kế kh&ocirc;ng giống bất kỳ mẫu n&agrave;o m&agrave; đội b&oacute;ng Old Trafford đ&atilde; mặc trước đ&acirc;y.</p>\r\n\r\n<p>Mẫu &aacute;o s&acirc;n nh&agrave; của Manchester United 2018 2019 kh&ocirc;ng giống với bất kỳ mẫu thiết kế n&agrave;o trước đ&oacute;. Thiết kế với d&agrave;i mảu đỏ từ tr&ecirc;n cổ &aacute;o l&agrave;m mờ dần xuống phần quần, bộ &aacute;o b&oacute;ng đ&aacute; Adidas Manchester United&nbsp; 2018 2019 nh&igrave;n thấy được nhiều chi tiết m&agrave;u đen được th&ecirc;m dưới dạng 3 sọc dưới quần, tr&ecirc;n vai v&agrave; một đường vi&ecirc;n quanh đỉnh cổ &aacute;o kiểu EQT. Biểu được Chevrolet ở mặt trước &aacute;o b&oacute;ng đ&aacute; Manchester United 2018 2019 c&oacute; m&agrave;u trắng.<br />\r\nPhần họa tiết ở mặt trước của bộ Manchester United 2019 bao gồm c&aacute;c h&igrave;nh lục gi&aacute;c c&oacute; k&iacute;ch thước kh&aacute;c nhau v&agrave; mờ dần th&agrave;nh một đường kẻ tinh tế hơn ở nửa tr&ecirc;n của &aacute;o. Mẫu c&oacute; thiết kế tinh tế, &aacute;o s&acirc;n nh&agrave; Manchester United 2019 dựa tr&ecirc;n thiết kế mới của Adidas Climachill</p>\r\n\r\n<p><img class=\"img-fluid\" src=\"https://dothethao.net.vn/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1.jpg\" srcset=\"https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1.jpg 1200w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-510x287.jpg 510w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-150x84.jpg 150w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-300x169.jpg 300w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-768x432.jpg 768w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-1024x576.jpg 1024w\" /></p>\r\n\r\n<p>&Aacute;o b&oacute;ng đ&aacute; MU 2019 2020 được thiết kế lấy cảm hứng từ hộp đ&ecirc;m &ldquo;Hacienda&rdquo; huyền thoại ở Manchester đ&atilde; ch&iacute;nh thức ra mắt. &Aacute;o b&oacute;ng đ&aacute; Manchester City s&acirc;n kh&aacute;ch 2019 2020 lấy m&agrave;u đen l&agrave;m chủ đạo với logo Puma m&agrave;u hồng đ&agrave;o ở ngực phải v&agrave; một miếng v&aacute; t&agrave;i trợ Etihad m&agrave;u v&agrave;ng s&aacute;ng ở giữa.</p>\r\n\r\n<p><img class=\"img-fluid\" src=\"https://dothethao.net.vn/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-1.jpg\" srcset=\"https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-1.jpg 1200w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-1-510x340.jpg 510w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-1-150x100.jpg 150w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-1-300x200.jpg 300w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-1-768x512.jpg 768w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-1-1024x683.jpg 1024w\" /></p>\r\n\r\n<h2>2/ &Aacute;o đấu Chelsea 2019/2020</h2>\r\n\r\n<p>Nếu l&agrave; fan h&acirc;m mộ của&nbsp;<strong>c&acirc;u lạc bộ Chelsea</strong>&nbsp;bạn ắt hẳn phải biết đến biệt danh&nbsp;<strong>The Blues</strong>. Gắn liền với sự ph&aacute;t triển cũng như th&agrave;nh c&ocirc;ng của c&acirc;u lạc bộ xanh dương lu&ocirc;n được mệnh danh l&agrave; m&agrave;u sắc của Chelsea. Ch&iacute;nh do yếu tố n&agrave;y n&ecirc;n Nike kh&ocirc;ng thể bỏ qua m&agrave;u xanh cho chiếc &aacute;o đấu của Chelsea. Bộ &aacute;o s&acirc;n nh&agrave; mới 2019-2020, được lấy cảm hứng rất nhiều từ Stamford Bridge, s&acirc;n nh&agrave; của c&acirc;u lạc bộ kể từ năm 1905. &Aacute;o b&oacute;ng đ&aacute; Chelsea 2019 2020 sẽ được ra mắt trong trận đấu cuối c&ugrave;ng của nh&agrave; M&ugrave;a giải M&ugrave;a giải Premier League, gặp Watford v&agrave;o ng&agrave;y 5 th&aacute;ng Năm.</p>\r\n\r\n<p>Cũng giống như hai m&ugrave;a trước, &aacute;o s&acirc;n nh&agrave; của Chelsea 2019-2020 chủ yếu l&agrave; &lsquo;m&agrave;u xanh nước biển&rsquo;. Họa tiết h&igrave;nh họa, tối hơn một ch&uacute;t được lấy cảm hứng từ Stamford Bridge.</p>\r\n\r\n<p>Họa tiết tr&ecirc;n &aacute;o l&agrave; h&igrave;nh ảnh hợp nhất của North Stand, East and West Stands v&agrave; Shed End th&agrave;nh một bố cục trừu trượng, độc đ&aacute;o. Phẩn cổ b&ecirc;n trong &aacute;o sơ mi 2019-2020 của Chelsea thấy được một con sư tử với d&ograve;ng chữ Pride of London Shaw được viết xung quanh n&oacute;. &Aacute;o b&oacute;ng đ&aacute; thi đấu clb chelsea được bổ sung một bảng m&agrave;u đỏ ph&iacute;a sau cổ với d&ograve;ng chữ &ldquo;CFC&rdquo;</p>\r\n\r\n<p><img class=\"img-fluid\" src=\"https://dothethao.net.vn/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-2.jpg\" srcset=\"https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-2.jpg 1200w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-2-510x364.jpg 510w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-2-150x107.jpg 150w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-2-300x214.jpg 300w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-2-768x549.jpg 768w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-2-1024x732.jpg 1024w\" /></p>\r\n\r\n<p>Mẫu &aacute;o b&oacute;ng đ&aacute; Chelsea s&acirc;n kh&aacute;ch 2019 2020 của Chelsea FC giới thiệu một c&aacute;i nh&igrave;n rất mới mẻ với &aacute;o truyền thống cổ polo, kết hợp ch&acirc;n đế m&agrave;u trắng với viền m&agrave;u xanh đỏ. Được sản xuất bởi Nike, &aacute;o b&oacute;ng đ&aacute; s&acirc;n kh&aacute;ch Chelsea 2019 2020 c&oacute; sự g&oacute;p mặt của Yokohama Tyres.</p>\r\n\r\n<p><img class=\"img-fluid\" src=\"https://dothethao.net.vn/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-2.jpg\" srcset=\"https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-2.jpg 806w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-2-510x759.jpg 510w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-2-101x150.jpg 101w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-2-202x300.jpg 202w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-2-768x1143.jpg 768w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-2-688x1024.jpg 688w\" /></p>\r\n\r\n<p>Với phong c&aacute;ch cổ điển, &aacute;o s&acirc;n kh&aacute;ch 2019 2020 cưa Chelsea c&oacute; m&agrave;u trắng với c&aacute;c điểm nhấn m&agrave;u xanh v&agrave; đỏ. Bộ &aacute;o c&oacute; cổ polo trắng v&agrave; cổ tay &aacute;o với viền m&agrave;u đỏ v&agrave; m&agrave;u xanh mỏng.</p>\r\n\r\n<p>Bộ &aacute;o b&oacute;ng đ&aacute; Chelsea s&acirc;n kh&aacute;ch 2019 2020 với thiết kế truyền thống được lấy cảm hứng từ &lsquo;Mods&rsquo; một n&eacute;t văn h&oacute;a nh&oacute;m ph&aacute;t triển mạnh ở London v&agrave;o những năm 1960 v&agrave; đặc biệt l&agrave; quanh đường King, c&aacute;ch Stamford Bridge kh&ocirc;ng xa.</p>\r\n\r\n<h2>3/ &Aacute;o đấu&nbsp;<strong>CLB Tottenham 2019/2020</strong></h2>\r\n\r\n<p>Bộ&nbsp;<strong>&aacute;o b&oacute;ng đ&aacute; Tottenham</strong>&nbsp;Hotspur s&acirc;n nh&agrave; 2019-2020 đ&atilde; được ra mắt, giới thiệu một thiết kế rất b&oacute;ng bẩy v&agrave; cổ điển với m&agrave;u trắng.</p>\r\n\r\n<p>Sau thiết kế gradient năm ngo&aacute;i, &aacute;o s&acirc;n nh&agrave; Tottenham 2019 2020 l&agrave; thiết kế mới nhất m&agrave; Nike gi&agrave;nh cho c&acirc;u lạc bộ. &Aacute;o c&oacute; m&agrave;u trắng với viền hải qu&acirc;n ở dạng c&ograve;ng tay &aacute;o, cổ &aacute;o v&agrave; băng cổ. Một c&acirc;u n&oacute;i ch&acirc;m ng&ocirc;n của c&acirc;u lạc bộ &lsquo;To Dare Is To Do&rsquo; xuất hiện ở cổ b&ecirc;n trong của bộ &aacute;o b&oacute;ng đ&aacute; s&acirc;n nh&agrave; mới. C&aacute;c nh&agrave; t&agrave;i trợ AIA c&oacute; m&agrave;u đỏ nổi bật nằm ngay giữa &aacute;o.</p>\r\n\r\n<p><img class=\"img-fluid\" src=\"https://dothethao.net.vn/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-3.jpg\" srcset=\"https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-3.jpg 1200w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-3-510x364.jpg 510w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-3-150x107.jpg 150w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-3-300x214.jpg 300w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-3-768x549.jpg 768w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-3-1024x732.jpg 1024w\" /></p>\r\n\r\n<p>Mẫu&nbsp;<strong>&aacute;o b&oacute;ng đ&aacute; s&acirc;n kh&aacute;ch Tottenham</strong>&nbsp;2019-2020 chủ yếu c&oacute; m&agrave;u xanh đậm với logo m&agrave;u trắng. Thật th&uacute; vị, một dải gradient m&agrave;u t&iacute;m tinh tế đang chạy tr&ecirc;n đỉnh của ngực l&agrave;m nổi bật bộ &aacute;o đấu Tottenham 2019 2020.</p>\r\n\r\n<p><img class=\"img-fluid\" src=\"https://dothethao.net.vn/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-4.jpg\" srcset=\"https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-4.jpg 1180w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-4-510x519.jpg 510w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-4-147x150.jpg 147w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-4-295x300.jpg 295w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-4-768x781.jpg 768w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-4-1007x1024.jpg 1007w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-4-60x60.jpg 60w\" /></p>\r\n\r\n<p>B&ecirc;n trong cổ &aacute;o l&agrave; d&ograve;ng chữ &lsquo;Audere est Facere&rsquo; m&agrave;u trắng được viết theo tiếng Latin l&agrave;&lsquo; To Dare Is To Do, c&acirc;u ch&acirc;m ng&ocirc;n của c&acirc;u lạc bộ. Một dải m&agrave;u t&iacute;m với một dải trắng mỏng chạy xuống sau g&aacute;y.</p>\r\n\r\n<p>&Aacute;o b&oacute;ng đ&aacute; s&acirc;n kh&aacute;ch mới của Tottenham Hotspur kết hợp m&agrave;u xanh v&agrave; m&agrave;u t&iacute;m, &ldquo;Hoppin n&oacute;i.&rdquo; Điều quan trọng, giống như truyền thống c&acirc;u lạc bộ, ch&uacute;ng t&ocirc;i lu&ocirc;n mong muốn thiết kế của m&igrave;nh sẽ l&agrave;m h&agrave;i l&ograve;ng vận động vi&ecirc;n v&agrave; người h&acirc;m mộ. &ldquo;</p>\r\n\r\n<h2>4/ Mẫu &aacute;o b&oacute;ng đ&aacute; Arsenal 2019/2020</h2>\r\n\r\n<p>Bộ&nbsp;<strong>&aacute;o b&oacute;ng đ&aacute; Arsenal</strong>&nbsp;s&acirc;n nh&agrave; 2019 2020 của Adidas đ&atilde; được ra mắt s&aacute;ng nay, c&acirc;u lạc bộ đ&atilde; phải đợi đến ng&agrave;y 1 th&aacute;ng 7 để ra mắt. &Aacute;o b&oacute;ng đ&aacute; Arsenal s&acirc;n nh&agrave; 2019 2020 lấy cảm hứng từ đầu những năm 1990 với phong c&aacute;ch cổ &aacute;o cổ điển kết hợp với m&agrave;u đỏ trắng hải qu&acirc;n</p>\r\n\r\n<p><strong>&Aacute;o b&oacute;ng đ&aacute; s&acirc;n nh&agrave; Arsenal</strong>&nbsp;2019 2020 được kết hợp giữa th&acirc;n m&agrave;u đỏ truyền thống v&agrave; tay &aacute;o trắng với viền hải qu&acirc;n tr&ecirc;n cổ &aacute;o v&agrave; cổ tay &aacute;o.</p>\r\n\r\n<p><img class=\"img-fluid\" src=\"https://dothethao.net.vn/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-4.jpg\" srcset=\"https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-4.jpg 890w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-4-510x261.jpg 510w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-4-150x77.jpg 150w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-4-300x154.jpg 300w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-4-768x393.jpg 768w\" /></p>\r\n\r\n<p>Năm nay&nbsp;<strong>&aacute;o b&oacute;ng đ&aacute; Arsenal s&acirc;n kh&aacute;ch</strong>&nbsp;2019 2020 được c&aacute;ch t&acirc;n thiết kế v&agrave; mang t&iacute;nh biểu tượng theo c&aacute;ch hiện đại. Mẫu &aacute;o năm nay chắc chắn sẽ được người h&acirc;m mộ v&ocirc; c&ugrave;ng th&iacute;ch th&uacute;.</p>\r\n\r\n<p>Mẫu &aacute;o s&acirc;n kh&aacute;ch của Arsenal 2019-2020 c&oacute; m&agrave;u v&agrave;ng được sử dụng l&agrave;m điểm nhấn, logo v&agrave; trang tr&iacute;. Thiết kế mang t&iacute;nh biểu tượng với biểu tượng ba sọc đ&atilde; l&agrave; một thương hiệu gắn liền với c&acirc;u lạc bộ từ giữa năm 1991 v&agrave; 1993. Mặc d&ugrave; thiết kế kh&aacute; t&aacute;o bạo v&agrave; kh&aacute;c thường, &aacute;o đấu s&acirc;n kh&aacute;ch của Arsenal vẫn được sự ủng hộ của c&aacute;c &ldquo;Ph&aacute;o Thủ&rdquo;</p>\r\n\r\n<p>Giải th&iacute;ch cho thiết kế bộ đồ s&acirc;n kh&aacute;ch Arsenal 2019-2020 đ&oacute; l&agrave; hướng tới thiết kế cổ điển, hơi giống với bộ World Cup 2018 của Đức, được lấy cảm hứng từ năm 1990</p>\r\n\r\n<p><img class=\"img-fluid\" sizes=\"(max-width: 781px) 100vw, 781px\" src=\"https://dothethao.net.vn/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-5.jpg\" srcset=\"https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-5.jpg 781w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-5-510x784.jpg 510w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-5-98x150.jpg 98w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-5-195x300.jpg 195w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-5-768x1179.jpg 768w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-5-667x1024.jpg 667w\" /></p>\r\n\r\n<h2>5/ Mẫu &aacute;o b&oacute;ng đ&aacute; Manchester City 2019 2020</h2>\r\n\r\n<p>Mẫu &aacute;o b&oacute;ng đ&aacute; s&acirc;n nh&agrave; của Man City 2019 2020 năm nay c&oacute; m&agrave;u xanh da trời kết hợp logo m&agrave;u t&iacute;m ở mặt trước v&agrave; một dải m&agrave;u t&iacute;m chạy từ tay &aacute;o xuống.</p>\r\n\r\n<p>Mẫu &aacute;o năm nay nổi bật d&ograve;ng chữ &lsquo;125 năm&rsquo; được in b&ecirc;n dưới huy hiệu c&acirc;u lạc bộ tr&ecirc;n ngực tr&aacute;i của Man City 2019 2020 để kỷ niệm ng&agrave;y th&agrave;nh lập c&acirc;u lạc bộ. &Aacute;o được phủ một lớp họa tiết tinh tế l&agrave;m n&ecirc;n phong c&aacute;ch bộ &aacute;o thi đấu Manchester City 2019 2020, đặc biệt năm nay thiết kế c&oacute; chữ V đơn giản.Quần Man City năm nay c&oacute; m&agrave;u trắng v&agrave; vớ xanh tạo th&agrave;nh chỉnh thể bộ &aacute;o thi đấu s&acirc;n nh&agrave; Manchester City 2019-2020.</p>\r\n\r\n<p>class=\"img-fluid\" sizes=\"(max-width: 800px) 100vw, 800px\" src=\"https://dothethao.net.vn/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-6.jpg\" srcset=\"https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-6.jpg 800w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-6-510x340.jpg 510w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-6-150x100.jpg 150w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-6-300x200.jpg 300w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-6-768x512.jpg 768w\" /></p>\r\n\r\n<p>&Aacute;o b&oacute;ng đ&aacute; Manchester City s&acirc;n kh&aacute;ch 2019 2020 năm nay được thiết kế lấy cảm hứng từ hộp đ&ecirc;m &ldquo;Hacienda&rdquo; huyền thoại ở Manchester đ&atilde; ch&iacute;nh thức ra mắt. &Aacute;o b&oacute;ng đ&aacute; Manchester City s&acirc;n kh&aacute;ch 2019 2020 lấy m&agrave;u đen l&agrave;m chủ đạo với logo Puma m&agrave;u hồng đ&agrave;o ở ngực phải v&agrave; một miếng v&aacute; t&agrave;i trợ Etihad m&agrave;u v&agrave;ng s&aacute;ng ở giữa.</p>\r\n\r\n<p>Dải m&agrave;u đen m&agrave;u v&agrave;ng được in b&ecirc;n trong tr&ecirc;n tay &aacute;o b&ecirc;n phải của &aacute;o b&oacute;ng đ&aacute; Manchester City s&acirc;n kh&aacute;ch 2019 2020 như một sự kết hợp chặt ch&eacute; giữa Manchester City với The Hacienda.</p>\r\n\r\n<p><img class=\"img-fluid\" sizes=\"(max-width: 1023px) 100vw, 1023px\" src=\"https://dothethao.net.vn/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-5.jpg\" srcset=\"https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-5.jpg 1023w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-5-510x287.jpg 510w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-5-150x84.jpg 150w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-5-300x169.jpg 300w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-5-768x432.jpg 768w\" /></p>\r\n\r\n<h2>6. &Aacute;o b&oacute;ng đ&aacute; Leicester City 2019 2020</h2>\r\n\r\n<p>Sau tiết lộ về &aacute;o b&oacute;ng đ&aacute; s&acirc;n nh&agrave; v&agrave;i tuần trước, Leicester City h&ocirc;m nay đ&atilde; tiết lộ hai bộ &aacute;o đấu 2019-20 của họ (c&oacute; hai bộ đồ s&acirc;n kh&aacute;ch &amp; mẫu thứ ba).&nbsp;Bộ đồng phục nh&agrave; LCFC mới c&oacute; m&agrave;u xanh truyền thống, trong khi bộ đồng phục thay thế l&agrave; than antraxit v&agrave; m&agrave;u hồng.</p>\r\n\r\n<p><img class=\"img-fluid\" sizes=\"(max-width: 1100px) 100vw, 1100px\" src=\"https://dothethao.net.vn/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-6.jpg\" srcset=\"https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-6.jpg 1100w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-6-510x287.jpg 510w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-6-150x84.jpg 150w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-6-300x169.jpg 300w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-6-768x432.jpg 768w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-6-1024x576.jpg 1024w\" /></p>\r\n\r\n<p><strong>&Aacute;o b&oacute;ng đ&aacute; Leicester s&acirc;n nh&agrave; 2019</strong>&nbsp;chủ yếu l&agrave; m&agrave;u xanh với t&ecirc;n nh&agrave; t&agrave;i trợ lớn King Power in tr&ecirc;n mặt trước v&agrave; sọc Adidas v&agrave;ng tr&ecirc;n vai. Quần short trắng v&agrave; vớ xanh ho&agrave;n th&agrave;nh bộ &aacute;o b&oacute;ng đ&aacute; Leicester City 2019-20.</p>\r\n\r\n<p><img class=\"img-fluid\" sizes=\"(max-width: 1050px) 100vw, 1050px\" src=\"https://dothethao.net.vn/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-2-1.jpg\" srcset=\"https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-2-1.jpg 1050w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-2-1-510x287.jpg 510w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-2-1-150x84.jpg 150w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-2-1-300x169.jpg 300w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-2-1-768x432.jpg 768w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-2-1-1024x576.jpg 1024w\" /></p>\r\n\r\n<p>Bộ&nbsp;<strong>&aacute;o b&oacute;ng đ&aacute; Leicester s&acirc;n kh&aacute;ch</strong>&nbsp;2019 &ndash; 2020 dựa tr&ecirc;n mẫu&nbsp;Campeon 19&nbsp;, m&agrave; Adidas dựa tr&ecirc;n bộ đồng phục thi đấu World Cup 2018 của Đức. C&acirc;u lạc bộ sau nhiều lần &uacute;p mở th&igrave; mẫu &aacute;o b&oacute;ng đ&aacute; thứ ba của Leicester 2019-2020 c&oacute; m&agrave;u hồng, với logo v&agrave; t&ecirc;n nh&agrave; t&agrave;i trợ xuất hiện với m&agrave;u đen, bao gồm một huy hiệu c&acirc;u lạc bộ m&agrave;u hồng đen tr&ecirc;n ngực tr&aacute;i.</p>\r\n\r\n<p><img class=\"img-fluid\" sizes=\"(max-width: 1050px) 100vw, 1050px\" src=\"https://dothethao.net.vn/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-7.jpg\" srcset=\"https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-7.jpg 1050w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-7-510x287.jpg 510w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-7-150x84.jpg 150w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-7-300x169.jpg 300w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-7-768x432.jpg 768w, https://hongphucsport.com/wp-content/uploads/2019/03/top-cac-mau-ao-dau-dep-nhat-giai-ngoai-hang-anh-2019-2020-1-7-1024x576.jpg 1024w\" /></p>\r\n\r\n<p>Ngo&agrave;i bộ &aacute;o b&oacute;ng đ&aacute; s&acirc;n kh&aacute;ch m&agrave;u hồng, Leicester City năm nay c&ograve;n cho ra mắt bộ &aacute;o b&oacute;ng đ&aacute; s&acirc;n kh&aacute;ch thứ 2 c&oacute; m&agrave;u đen.</p>\r\n\r\n<p>Dựa tr&ecirc;n mẫu&nbsp;Tiro 19&nbsp;với m&agrave;u x&aacute;m đen v&agrave; trắng, &aacute;o s&acirc;n kh&aacute;ch của Leicester 2019-2020 giới thiệu một c&aacute;i nh&igrave;n mới mẻ với một mẫu thiết kế rất tinh tế v&agrave; trang tr&iacute; tr&ecirc;n cổ &aacute;o v&agrave; ở dạng 3 sọc b&ecirc;n.&nbsp;Quần short v&agrave; vớ ho&agrave;n th&agrave;nh mẫu &aacute;o s&acirc;n kh&aacute;ch mới của Leicester City 2019-2020.</p>\r\n\r\n<p>Tr&ecirc;n đ&acirc;y l&agrave; c&aacute;c mẫu &aacute;o b&oacute;ng đ&aacute; cực chất được nhận x&eacute;t l&agrave; đẹp nhất trong m&ugrave;a giải 2019/2020. Sau khi chi&ecirc;m ngưỡng những mẫu &aacute;o đấu n&agrave;y liệu bạn c&oacute; bị m&ecirc; hoặc.</p>\r\n', '../template4.3.1/images/AnhTinTuc/anh1tintuc2.jpg', '2019-10-30', 'Quản trị viên', 1, 6),
(3, 'Áo bóng đá Juventus mẫu thứ tư 2019 2020 màu trắng sọc đen', '<p>Sau trận đấu tại Serie A với Genova, Adidas hiện đ&atilde; th&ecirc;m&nbsp;<strong>&aacute;o b&oacute;ng đ&aacute; mẫu thứ tư Juventus 2019 2020</strong>&nbsp;tr&ecirc;n trang web của m&igrave;nh với gi&aacute;: 180 Euro. N&oacute; dường như l&agrave; một phi&ecirc;n bản rất hạn chế &ndash; tr&ecirc;n thực tế, đ&oacute; l&agrave; một phi&ecirc;n bản &ldquo;WAITING ROOM RelEASE&rdquo; của Adidas.&nbsp;</p>\r\n\r\n<h2>Mẫu &aacute;o b&oacute;ng đ&aacute; mẫu thứ tư Juventus 2019 2020</h2>\r\n\r\n<p><img class=\"img-fluid\" width=\"600\" height=\"100%\" src=\"https://dothethao.net.vn/wp-content/uploads/2019/11/ao-bong-da-juventus-mau-thu-tu-2019-2020-mau-trang-soc-den.jpg\" srcset=\"https://dothethao.net.vn/wp-content/uploads/2019/11/ao-bong-da-juventus-mau-thu-tu-2019-2020-mau-trang-soc-den.jpg 590w, https://dothethao.net.vn/wp-content/uploads/2019/11/ao-bong-da-juventus-mau-thu-tu-2019-2020-mau-trang-soc-den-320x400.jpg 320w\" title=\"Ronaldo xuất hiện với mẫu áo thứ tư tại Serie A với Genova\" width=\"900\" /></p>\r\n\r\n<p>Ronaldo xuất hiện với mẫu &aacute;o thứ tư tại Serie A với Genova</p>\r\n\r\n<p>Bộ đồ Adidas x Palace thứ tư mới của Juventus vừa được c&acirc;u lạc bộ &Yacute; tiết lộ v&agrave; hiện đang được ra mắt trong trận đấu tại Serie A với Genova.</p>\r\n\r\n<p><img class=\"img-fluid\" width=\"600\" height=\"100%\" src=\"https://dothethao.net.vn/wp-content/uploads/2019/11/ao-bong-da-juventus-mau-thu-tu-2019-2020-mau-trang-soc-den-1.jpg\" srcset=\"https://dothethao.net.vn/wp-content/uploads/2019/11/ao-bong-da-juventus-mau-thu-tu-2019-2020-mau-trang-soc-den-1.jpg 738w, https://dothethao.net.vn/wp-content/uploads/2019/11/ao-bong-da-juventus-mau-thu-tu-2019-2020-mau-trang-soc-den-1-600x600.jpg 600w, https://dothethao.net.vn/wp-content/uploads/2019/11/ao-bong-da-juventus-mau-thu-tu-2019-2020-mau-trang-soc-den-1-100x100.jpg 100w, https://dothethao.net.vn/wp-content/uploads/2019/11/ao-bong-da-juventus-mau-thu-tu-2019-2020-mau-trang-soc-den-1-400x400.jpg 400w, https://dothethao.net.vn/wp-content/uploads/2019/11/ao-bong-da-juventus-mau-thu-tu-2019-2020-mau-trang-soc-den-1-280x280.jpg 280w\" title=\"Áo đấu Juventus mẫu thứ tư 2019 2020 màu trắng sọc đen\" width=\"900\" /></p>\r\n\r\n<p>&Aacute;o đấu Juventus mẫu thứ tư 2019 2020 m&agrave;u trắng sọc đen</p>\r\n\r\n<p>Mang n&eacute;t hiện đại tr&ecirc;n sọc Juventus cổ điển, &aacute;o đấu thứ tư mới của Juventus 2019 &ndash; 2020 c&oacute; ba sọc đen tr&ecirc;n nền trắng, với c&aacute;c điểm nhấn m&agrave;u cam. C&aacute;c sọc ở mặt trước của mẫu &aacute;o thứ tư 2019 2020 của Juventus mờ dần về ph&iacute;a dưới trong khi họa tiết tương tự cũng xuất hiện ở tr&ecirc;n tất. Quần short trắng v&agrave; vớ ho&agrave;n th&agrave;nh vẻ ngo&agrave;i của dải thứ tư mới Juventus 2019 2020 của Adidas x Palace.</p>\r\n\r\n<p><img class=\"img-fluid\" width=\"600\" height=\"100%\" src=\"https://dothethao.net.vn/wp-content/uploads/2019/11/ao-bong-da-juventus-mau-thu-tu-2019-2020-mau-trang-soc-den-2.jpg\" srcset=\"https://dothethao.net.vn/wp-content/uploads/2019/11/ao-bong-da-juventus-mau-thu-tu-2019-2020-mau-trang-soc-den-2.jpg 1522w, https://dothethao.net.vn/wp-content/uploads/2019/11/ao-bong-da-juventus-mau-thu-tu-2019-2020-mau-trang-soc-den-2-600x368.jpg 600w, https://dothethao.net.vn/wp-content/uploads/2019/11/ao-bong-da-juventus-mau-thu-tu-2019-2020-mau-trang-soc-den-2-653x400.jpg 653w, https://dothethao.net.vn/wp-content/uploads/2019/11/ao-bong-da-juventus-mau-thu-tu-2019-2020-mau-trang-soc-den-2-1305x800.jpg 1305w, https://dothethao.net.vn/wp-content/uploads/2019/11/ao-bong-da-juventus-mau-thu-tu-2019-2020-mau-trang-soc-den-2-768x471.jpg 768w\" title=\"Mẫu áo thứ tư của clb Juventus mùa giải 2019 2020 \" width=\"900\" /></p>\r\n\r\n<p>Mẫu &aacute;o thứ tư của clb Juventus m&ugrave;a giải 2019 2020</p>\r\n\r\n<p>Mẫu &aacute;o thứ tư n&agrave;y nổi bật hơn cả l&agrave; m&agrave;u xanh tr&ecirc;n 3 logo của clb, nh&agrave; sản xuất, nh&agrave; t&agrave;i trợ thực sự ấn tượng khi nh&igrave;n v&agrave;o. M&agrave;u xanh n&agrave;y kh&aacute; s&aacute;ng với t&ocirc;ng m&agrave;u phản quang l&agrave; một trong những bộ &aacute;o ấn tượng của Juventus năm nay.</p>\r\n\r\n<p>Mẫu &aacute;o thứ tư Juvetus đ&atilde; xuất hiện, bạn c&oacute; th&iacute;ch mẫu &aacute;o đấu mới n&agrave;y của Juventus h&atilde;y để lại b&igrave;nh luận của m&igrave;nh ở b&ecirc;n dưới nh&eacute;. Ngo&agrave;i ra bạn c&oacute; thể tham khảo c&aacute;c mẫu&nbsp;<a href=\"https://dothethao.net.vn/san-pham/ao-bong-da-juventus-san-nha-2019-2020-mau-thu-2/\">&aacute;o b&oacute;ng đ&aacute; Juventus 2019 2020</a>&nbsp;cũng đang được b&agrave;y b&aacute;n nh&eacute;.</p>\r\n', '../template4.3.1/images/AnhTinTuc/anhtintuc3.jpg', '2019-10-30', 'Quản trị viên', 1, 8),
(4, 'Top Những Siêu Phẩm Áo Đấu Tại Bundesliga 2019 – 2020', 'Mùa giải 2018 khép lại chưa lâu các câu lạc bộ tham dự giải đấu Bundesliga bắt đầu rục rịch cho ra mắt những mẫu chiến phục của mùa giải 2019 – 2020. Bundesliga là một giải bóng đá chuyên nghiệp của Đức. Được mệnh danh là một trong những giải đấu được mong chờ nhất hành tinh Bundesliga mùa giải năm nay dự tính sẽ tạo nên nhiều bất ngờ cho fan hâm mộ của giải đấu này.\r\nTrước khi chứng kiến những trận cầu đỉnh cao hãy cùng chúng tôi khám phá những siêu phẩm áo đấu được các câu lạc bộ thi đấu tại Bundesliga trình làng vào đầu hè vừa rồi nhé. Theo đánh giá chung của giới chuyên môn cũng như những phản hồi từ phía cổ động viên của giải đấu Bundesliga cho hay mùa giải 2019 – 2020 năm nay các câu lạc bộ đều có sự thay đổi lớn trong các mẫu áo đấu của CLB mình. Nổi bật trong số đó phải kể đến loạt siêu phẩm đến từ các ông lớn tại Bundesliga như Dortmund, Bayern Munich,  Schalke là những mẫu áo đấu được đánh giá cực cao trong mùa giải năm nay.', '../template4.3.1/images/AnhTinTuc/topaodaubundesliga.jpg', '2019-10-30', 'Quản trị viên', 1, 14);
INSERT INTO `tbl_tintuc` (`ID`, `TieuDe`, `NoiDung`, `HinhDaiDien`, `NgayDang`, `NguoiDang`, `IDLoaiTinTuc`, `viewcount`) VALUES
(5, 'Khám phá 12 sân vận động siêu sang phục vụ World Cup 2022 tại Qatar', '<p>Cả thế giới vừa tiếc nuối chia tay World Cup 2018 tại Nga với nụ cười của những người Ph&aacute;p. Trong khi người h&acirc;m mộ tr&ecirc;n to&agrave;n thế giới phải chờ đợi 4 năm nữa để được tận hưởng những bữa tiệc b&oacute;ng đ&aacute; của những trận cầu đỉnh cao ở World Cup, th&igrave; người d&acirc;n Qatar đ&atilde; bắt tay ngay v&agrave;o với dự &aacute;n x&acirc;y dựng v&agrave; sửa chữa một loạt c&aacute;c s&acirc;n vận động với mức kinh ph&iacute; khủng nhất từ trước đến nay &ndash; 220 tỷ USD để phục vụ cho World Cup 2022.</p>\r\n\r\n<p><img sizes=\"(max-width: 600px) 100vw, 600px\" src=\"https://dothethao.net.vn/wp-content/uploads/2018/07/37062792_1881183185235952_4487784999440875520_o.jpg\" srcset=\"https://dothethao.net.vn/wp-content/uploads/2018/07/37062792_1881183185235952_4487784999440875520_o.jpg 600w, https://dothethao.net.vn/wp-content/uploads/2018/07/37062792_1881183185235952_4487784999440875520_o-150x127.jpg 150w, https://dothethao.net.vn/wp-content/uploads/2018/07/37062792_1881183185235952_4487784999440875520_o-300x255.jpg 300w\" title=\"Khám phá 12 sân vận động siêu sang phục vụ World Cup 2022 \" width=\"600\" /></p>\r\n\r\n<p><em>Kh&aacute;m ph&aacute; 12 s&acirc;n vận động si&ecirc;u sang phục vụ World Cup 2022</em></p>\r\n\r\n<p>L&agrave; một quốc gia gi&agrave;u c&oacute; nhờ sở hữu trữ lượng &ldquo;v&agrave;ng đen&rdquo; h&agrave;ng đầu thế giới, Qatar gần như c&oacute; đủ t&agrave;i ch&iacute;nh để x&acirc;y dựng c&aacute;c s&acirc;n vận động quy m&ocirc; lớn, đẹp mắt v&agrave; ho&agrave;nh tr&aacute;ng. Những s&acirc;n vận động n&agrave;y kh&ocirc;ng chỉ l&agrave; nơi thi đấu, đ&oacute; hứa hẹn sẽ l&agrave; những biểu tượng mới về kiến tr&uacute;c của Qatar.</p>\r\n\r\n<p>Theo&nbsp;<em>WonderfulEngineering</em>, dưới đ&acirc;y l&agrave; phối cảnh của tất cả s&acirc;n vận động đang chuẩn bị được x&acirc;y dựng để ch&agrave;o đ&oacute;n c&aacute;c đội tuyển v&agrave; fan h&acirc;m mộ b&oacute;ng đ&aacute; tr&ecirc;n to&agrave;n thế giới đổ về sau hơn 4 năm tới.</p>\r\n\r\n<p>Xem th&ecirc;m&nbsp;<strong><a href=\"https://www.youtube.com/watch?v=b9SG3zmwRt8\">tại đ&acirc;y</a></strong>.</p>\r\n\r\n<h3><strong>1. S&acirc;n vận động Lusail Iconic</strong></h3>\r\n\r\n<p><em>Sức chứa dự kiến: 86.250 người</em></p>\r\n\r\n<p><img sizes=\"(max-width: 600px) 100vw, 600px\" src=\"https://dothethao.net.vn/wp-content/uploads/2018/07/6_79101.jpg\" srcset=\"https://dothethao.net.vn/wp-content/uploads/2018/07/6_79101.jpg 600w, https://dothethao.net.vn/wp-content/uploads/2018/07/6_79101-150x112.jpg 150w, https://dothethao.net.vn/wp-content/uploads/2018/07/6_79101-300x225.jpg 300w\" title=\"Sân vận động Lusail Iconic\" width=\"600\" /></p>\r\n\r\n<p><em>S&acirc;n vận động Lusail Iconic</em></p>\r\n\r\n<p>S&acirc;n vận động Lusail Iconic sẽ l&agrave; nơi tổ chức lễ khai mạc v&agrave; lễ bế mạc World Cup 2022. Đặc biệt, Lusail Iconic sẽ được x&acirc;y dựng tr&ecirc;n mặt nước v&agrave; được l&agrave;m m&aacute;t bằng c&aacute;ch sử dụng tấm pin mặt trời.</p>\r\n\r\n<h3><strong>2. S&acirc;n vận động Al-Shamal</strong></h3>\r\n\r\n<p><em>Sức chứa dự kiến: 45.120 người</em></p>\r\n\r\n<p><img sizes=\"(max-width: 600px) 100vw, 600px\" src=\"https://dothethao.net.vn/wp-content/uploads/2018/07/2_74454.jpg\" srcset=\"https://dothethao.net.vn/wp-content/uploads/2018/07/2_74454.jpg 600w, https://dothethao.net.vn/wp-content/uploads/2018/07/2_74454-150x103.jpg 150w, https://dothethao.net.vn/wp-content/uploads/2018/07/2_74454-300x207.jpg 300w\" title=\"Sân vận động Al-Shamal\" width=\"600\" /></p>\r\n\r\n<p><em>S&acirc;n vận động Al-Shamal</em></p>\r\n\r\n<p>Chỉ c&aacute;ch biển một đoạn ngắn, s&acirc;n vận động Al-Shamal được thiết kế với h&igrave;nh dạng của một chiếc thuyền truyền thống. Sau World Cup 2022, s&acirc;n vận động n&agrave;y sẽ l&agrave; nơi phục vụ cho những trận b&oacute;ng đ&aacute; giao hữu, kết t&igrave;nh b&egrave; bạn năm ch&acirc;u.</p>\r\n\r\n<h3><strong>3. S&acirc;n vận động Al-Gharrafa</strong></h3>\r\n\r\n<p><em>Sức chứa dự kiến: 44.740 người</em></p>\r\n\r\n<p><img sizes=\"(max-width: 600px) 100vw, 600px\" src=\"https://dothethao.net.vn/wp-content/uploads/2018/07/3_116441.jpg\" srcset=\"https://dothethao.net.vn/wp-content/uploads/2018/07/3_116441.jpg 600w, https://dothethao.net.vn/wp-content/uploads/2018/07/3_116441-150x106.jpg 150w, https://dothethao.net.vn/wp-content/uploads/2018/07/3_116441-300x212.jpg 300w\" title=\"Sân vận động Al-Gharrafa\" width=\"600\" /></p>\r\n\r\n<p><em>S&acirc;n vận động Al-Gharrafa</em></p>\r\n\r\n<p>Mặt tiền của s&acirc;n vận động Al-Gharrafa được thiết kế với nhiều m&agrave;u sắc rực rỡ, tr&ocirc;ng rất bắt mắt. S&acirc;n vận động n&agrave;y hiện đang được sử dụng l&agrave;m đấu trường thể thao v&agrave; từ nay đến năm 2022 n&oacute; sẽ được mở rộng để phục vụ cho World Cup.</p>\r\n\r\n<h3><strong>4. S&acirc;n vận động Education City</strong></h3>\r\n\r\n<p><em>Sức chứa dự kiến: 45.350 người</em></p>\r\n\r\n<p><img sizes=\"(max-width: 600px) 100vw, 600px\" src=\"https://dothethao.net.vn/wp-content/uploads/2018/07/4_48195.jpg\" srcset=\"https://dothethao.net.vn/wp-content/uploads/2018/07/4_48195.jpg 600w, https://dothethao.net.vn/wp-content/uploads/2018/07/4_48195-150x86.jpg 150w, https://dothethao.net.vn/wp-content/uploads/2018/07/4_48195-300x171.jpg 300w\" title=\"Sân vận động Education City\" width=\"600\" /></p>\r\n\r\n<p><em>S&acirc;n vận động Education City</em></p>\r\n\r\n<h3><strong>5. S&acirc;n vận động Al-Khor</strong></h3>\r\n\r\n<p><em>Sức chứa dự kiến: 45.330 người</em></p>\r\n\r\n<p><img sizes=\"(max-width: 600px) 100vw, 600px\" src=\"https://dothethao.net.vn/wp-content/uploads/2018/07/imageresize-4.jpg\" srcset=\"https://dothethao.net.vn/wp-content/uploads/2018/07/imageresize-4.jpg 600w, https://dothethao.net.vn/wp-content/uploads/2018/07/imageresize-4-150x94.jpg 150w, https://dothethao.net.vn/wp-content/uploads/2018/07/imageresize-4-300x188.jpg 300w\" title=\"Sân vận động Al-Khor\" width=\"600\" /></p>\r\n\r\n<p><em>S&acirc;n vận động Al-Khor</em></p>\r\n\r\n<p>S&acirc;n vận động Al-Khor cung cấp dịch vụ bảo hiểm cho tất cả c&aacute;c ghế ngồi tr&ecirc;n s&acirc;n. S&acirc;n vận động n&agrave;y được thiết kế theo h&igrave;nh dạng của một vỏ s&ograve; khổng lồ kh&ocirc;ng đối xứng. B&atilde;i đậu xe của s&acirc;n vận động c&oacute; sức chứa hơn 4.000 xe tư nh&acirc;n v&agrave; xe c&ocirc;ng cộng.</p>\r\n\r\n<h3><strong>6. S&acirc;n vận động Sport City</strong></h3>\r\n\r\n<p><em>Sức chứa dự kiến: 47.560 người</em></p>\r\n\r\n<p><img sizes=\"(max-width: 600px) 100vw, 600px\" src=\"https://dothethao.net.vn/wp-content/uploads/2018/07/7_112065.jpg\" srcset=\"https://dothethao.net.vn/wp-content/uploads/2018/07/7_112065.jpg 600w, https://dothethao.net.vn/wp-content/uploads/2018/07/7_112065-150x86.jpg 150w, https://dothethao.net.vn/wp-content/uploads/2018/07/7_112065-300x171.jpg 300w\" title=\"Sân vận động Sport City\" width=\"600\" /></p>\r\n\r\n<p><em>S&acirc;n vận động Sport City</em></p>\r\n\r\n<p>Sport City sẽ được thiết kế bởi kiến tr&uacute;c sư nổi tiếng người Mỹ Dan Meiswater. Hiện tại, s&acirc;n vận động n&agrave;y đ&atilde; thu h&uacute;t được 1.5 tỷ USD từ c&aacute;c nh&agrave; đầu tư.</p>\r\n\r\n<h3><strong>7. S&acirc;n vận động Al-Rayyan</strong></h3>\r\n\r\n<p><em>Sức chứa dự kiến: 44.740 người</em></p>\r\n\r\n<p><img sizes=\"(max-width: 600px) 100vw, 600px\" src=\"https://dothethao.net.vn/wp-content/uploads/2018/07/8_73153.jpg\" srcset=\"https://dothethao.net.vn/wp-content/uploads/2018/07/8_73153.jpg 600w, https://dothethao.net.vn/wp-content/uploads/2018/07/8_73153-150x106.jpg 150w, https://dothethao.net.vn/wp-content/uploads/2018/07/8_73153-300x212.jpg 300w\" title=\"Sân vận động Al-Rayyan\" width=\"600\" /></p>\r\n\r\n<p><em>S&acirc;n vận động Al-Rayyan</em></p>\r\n\r\n<p>Al-Rayyan l&agrave; một trong những s&acirc;n vận động lớn nhất th&agrave;nh phố hiện nay. Tuy nhi&ecirc;n, sức chứa của n&oacute; chỉ l&agrave; 22.000 chỗ ngồi. Đến World Cup 2022, Al-Rayyan sẽ c&oacute; sức chứa l&ecirc;n đến 44.740 chỗ ngồi với việc bổ sung một h&agrave;ng ghế đầu c&oacute; thể th&aacute;o rời khi cần thiết.</p>\r\n\r\n<h3><strong>8. S&acirc;n vận động Qatar University</strong></h3>\r\n\r\n<p><em>Sức chứa dự kiến: 43.520 người</em></p>\r\n\r\n<p><img sizes=\"(max-width: 600px) 100vw, 600px\" src=\"https://dothethao.net.vn/wp-content/uploads/2018/07/9_247985.jpg\" srcset=\"https://dothethao.net.vn/wp-content/uploads/2018/07/9_247985.jpg 600w, https://dothethao.net.vn/wp-content/uploads/2018/07/9_247985-150x99.jpg 150w, https://dothethao.net.vn/wp-content/uploads/2018/07/9_247985-300x198.jpg 300w\" title=\"Sân vận động Qatar University\" width=\"600\" /></p>\r\n\r\n<p><em>S&acirc;n vận động Qatar University</em></p>\r\n\r\n<p>S&acirc;n vận động Qatar University, nằm ở trung t&acirc;m của thủ đ&ocirc;, s&acirc;n vận động n&agrave;y sẽ đ&aacute;p ứng gần 45.000 kh&aacute;n giả.</p>\r\n\r\n<h3><strong>9. S&acirc;n vận động Umm Slal</strong></h3>\r\n\r\n<p><em>Sức chứa dự kiến: 45.120 người</em></p>\r\n\r\n<p><img sizes=\"(max-width: 600px) 100vw, 600px\" src=\"https://dothethao.net.vn/wp-content/uploads/2018/07/10_99676.jpg\" srcset=\"https://dothethao.net.vn/wp-content/uploads/2018/07/10_99676.jpg 600w, https://dothethao.net.vn/wp-content/uploads/2018/07/10_99676-150x106.jpg 150w, https://dothethao.net.vn/wp-content/uploads/2018/07/10_99676-300x212.jpg 300w\" title=\"Sân vận động Umm Slal\" width=\"600\" /></p>\r\n\r\n<p><em>S&acirc;n vận động Umm Slal</em></p>\r\n\r\n<p>Dựa tr&ecirc;n một ph&aacute;o đ&agrave;i Ả Rập truyền thống, s&acirc;n vận động Umm Salal l&agrave; một trong những s&acirc;n vận động c&oacute; những dịch vụ sang trọng nhất ở World Cup 2022 của Qatar. Sau giải đấu, s&acirc;n vận động n&agrave;y sẽ được sử dụng l&agrave;m s&acirc;n nh&agrave; của c&acirc;u lạc bộ Umm Slal FC.</p>\r\n\r\n<h3><strong>10. S&acirc;n vận động Khalifa International</strong></h3>\r\n\r\n<p><em>Sức chứa dự kiến: 68.030 người</em></p>\r\n\r\n<p><img sizes=\"(max-width: 600px) 100vw, 600px\" src=\"https://dothethao.net.vn/wp-content/uploads/2018/07/1_215772.jpg\" srcset=\"https://dothethao.net.vn/wp-content/uploads/2018/07/1_215772.jpg 600w, https://dothethao.net.vn/wp-content/uploads/2018/07/1_215772-150x100.jpg 150w, https://dothethao.net.vn/wp-content/uploads/2018/07/1_215772-300x200.jpg 300w\" title=\"Sân vận động Khalifa International\" width=\"600\" /></p>\r\n\r\n<p><em>S&acirc;n vận động Khalifa International</em></p>\r\n\r\n<h3><strong>11. S&acirc;n vận động Doha Port</strong></h3>\r\n\r\n<p><em>Sức chứa dự kiến: 44.950 người</em></p>\r\n\r\n<p><img sizes=\"(max-width: 600px) 100vw, 600px\" src=\"https://dothethao.net.vn/wp-content/uploads/2018/07/12_80159.jpg\" srcset=\"https://dothethao.net.vn/wp-content/uploads/2018/07/12_80159.jpg 600w, https://dothethao.net.vn/wp-content/uploads/2018/07/12_80159-150x106.jpg 150w, https://dothethao.net.vn/wp-content/uploads/2018/07/12_80159-300x212.jpg 300w\" title=\"Sân vận động Doha Port\" width=\"600\" /></p>\r\n\r\n<p><em>S&acirc;n vận động Doha Port</em></p>\r\n\r\n<p>Với sức chứa l&ecirc;n đến 45.000 chỗ ngồi, s&acirc;n vận động Doha Port nằm ở ngo&agrave;i khơi bờ biển của thủ đ&ocirc; Qatar. Xung quanh s&acirc;n vận động, ban dự &aacute;n sẽ x&acirc;y dựng một loạt c&aacute;c bến cảng v&agrave; c&aacute;c khu vui chơi giải tr&iacute; phục vụ người d&acirc;n tr&ecirc;n to&agrave;n thế giới ở World Cup 2022.</p>\r\n\r\n<h3><strong>12. S&acirc;n vận động Al-Wakrah</strong></h3>\r\n\r\n<p><em>Sức chứa dự kiến: 45.120 người</em></p>\r\n\r\n<p><img sizes=\"(max-width: 600px) 100vw, 600px\" src=\"https://dothethao.net.vn/wp-content/uploads/2018/07/13_96073.jpg\" srcset=\"https://dothethao.net.vn/wp-content/uploads/2018/07/13_96073.jpg 600w, https://dothethao.net.vn/wp-content/uploads/2018/07/13_96073-150x88.jpg 150w, https://dothethao.net.vn/wp-content/uploads/2018/07/13_96073-300x176.jpg 300w\" title=\"Sân vận động Al-Wakrah\" width=\"600\" /></p>\r\n\r\n<p><em>S&acirc;n vận động Al-Wakrah</em></p>\r\n\r\n<p>S&acirc;n vận động Al-Wakrah đang được sửa chữa v&agrave; được thiết kế lại bởi kiến tr&uacute;c sư nổi tiếng Zaha Hadid. Sau khi ho&agrave;n th&agrave;nh, Al-Wakrah sẽ c&oacute; sức chứa l&ecirc;n đến 45.000 chỗ ngồi.</p>\r\n', '../template4.3.1/images/AnhTinTuc/kienthucsp1.jpg', '2019-12-14', 'Quản trị viên', 2, 3),
(6, 'Lộ Giày vàng World Cup 2018, Messi và Neymar háo hức nhất', '<p>Chiếc gi&agrave;y v&agrave;ng d&agrave;nh cho Vua ph&aacute; lưới World Cup 2018 đ&atilde; lộ diện v&agrave; Messi, Neymar l&agrave; hai ứng vi&ecirc;n nặng k&yacute; nhất cho danh hiệu c&aacute; nh&acirc;n đ&aacute;ng mơ ước n&agrave;y.</p>\r\n\r\n<p><img src=\"https://dothethao.net.vn/wp-content/uploads/2018/06/4d3cdc4e00000578-5845457-image-a-81529000816707-1529068782484890778572.jpg\" srcset=\"https://dothethao.net.vn/wp-content/uploads/2018/06/4d3cdc4e00000578-5845457-image-a-81529000816707-1529068782484890778572.jpg 600w, https://dothethao.net.vn/wp-content/uploads/2018/06/4d3cdc4e00000578-5845457-image-a-81529000816707-1529068782484890778572-150x84.jpg 150w, https://dothethao.net.vn/wp-content/uploads/2018/06/4d3cdc4e00000578-5845457-image-a-81529000816707-1529068782484890778572-300x169.jpg 300w\" title=\"3 siêu sao được dự đoán có khả năng cao nhất dành Giày Vàng World Cup 2018\" width=\"600\" /></p>\r\n\r\n<p><em>3 si&ecirc;u sao được dự đo&aacute;n c&oacute; khả năng cao nhất d&agrave;nh Gi&agrave;y V&agrave;ng World Cup 2018</em></p>\r\n\r\n<p>H&igrave;nh d&aacute;ng Chiếc gi&agrave;y v&agrave;ng &ndash; phần thưởng d&agrave;nh cho Vua ph&aacute; lưới&nbsp;World Cup 2018&nbsp;đ&atilde; được lộ diện khi được trưng b&agrave;y ở Moscow. Đ&acirc;y l&agrave; danh hiệu c&aacute; nh&acirc;n đ&aacute;ng mơ ước nhất tại giải đấu b&oacute;ng đ&aacute; lớn nhất h&agrave;nh tinh. Cầu thủ ghi nhiều b&agrave;n thắng nhất giải đấu sẽ được trao danh hiệu n&agrave;y.</p>\r\n\r\n<p><img height=\"400\" sizes=\"(max-width: 600px) 100vw, 600px\" src=\"https://dothethao.net.vn/wp-content/uploads/2018/06/photo-1-1528300423124473359151.jpg\" srcset=\"https://dothethao.net.vn/wp-content/uploads/2018/06/photo-1-1528300423124473359151.jpg 600w, https://dothethao.net.vn/wp-content/uploads/2018/06/photo-1-1528300423124473359151-150x100.jpg 150w, https://dothethao.net.vn/wp-content/uploads/2018/06/photo-1-1528300423124473359151-300x200.jpg 300w\" title=\"Danh hiệu Giày vàng dành cho Vua phá lưới World Cup 2018 đã được trưng bày tại Moscow\" width=\"600\" /></p>\r\n\r\n<p><em>Danh hiệu Gi&agrave;y v&agrave;ng d&agrave;nh cho Vua ph&aacute; lưới World Cup 2018 đ&atilde; được trưng b&agrave;y tại Moscow</em></p>\r\n\r\n<p>Ở VCK World Cup 2018,&nbsp;Messi&nbsp;v&agrave; Neymar l&agrave; hai c&aacute;i t&ecirc;n s&aacute;ng gi&aacute; cho danh hiệu Vua ph&aacute; lưới. C&aacute;c nh&agrave; c&aacute;i đ&atilde; chọn tỉ lệ 10/1 cho cả Messi v&agrave; Neymar &ndash; hai ng&ocirc;i sao s&aacute;ng gi&aacute; nhất của ĐT Argentina v&agrave; ĐT Brazil, v&agrave; cũng l&agrave; hai ch&acirc;n s&uacute;t h&agrave;ng đầu ở m&ugrave;a giải 2017/18 vừa qua.</p>\r\n\r\n<p>Xếp ngay sau Messi v&agrave; Neymar l&agrave; Griezmann của ĐT Ph&aacute;p, kế đến l&agrave; Gabriel Jesus &ndash; cũng của ĐT Brazil v&agrave; Harry Kane của ĐT Anh. Đ&aacute;ng n&oacute;i, si&ecirc;u sao số 1 thế giới Cris Ronaldo chỉ xếp thứ 7 với tỉ lệ 16/1, ngang với Lukaku của ĐT Bỉ. Đặc biệt, ch&acirc;n s&uacute;t Timo Werner của ĐT Đức được đ&aacute;nh gi&aacute; rất cao, đứng tr&ecirc;n cả Ronaldo.</p>\r\n\r\n<p>Ở kỳ World Cup gần nhất c&aacute;ch đ&acirc;y 4 năm, James Rodriguez l&agrave; chủ nh&acirc;n của danh hiệu n&agrave;y sau khi ghi được 6 b&agrave;n thắng cho ĐT Colombia. Trước đ&oacute;, hai họng ph&aacute;o c&ugrave;ng của ĐT Đức l&agrave; Thomas Muller v&agrave; Miroslav Klose lần lượt gi&agrave;nh danh hiệu Vua ph&aacute; lưới World Cup 2010 v&agrave; 2006.</p>\r\n\r\n<p><img height=\"583\" sizes=\"(max-width: 600px) 100vw, 600px\" src=\"https://dothethao.net.vn/wp-content/uploads/2018/06/photo-1-15283004081821321543631.jpg\" srcset=\"https://dothethao.net.vn/wp-content/uploads/2018/06/photo-1-15283004081821321543631.jpg 600w, https://dothethao.net.vn/wp-content/uploads/2018/06/photo-1-15283004081821321543631-150x146.jpg 150w, https://dothethao.net.vn/wp-content/uploads/2018/06/photo-1-15283004081821321543631-300x292.jpg 300w\" title=\"James Rodriguez ĐT Colombia đang là cầu thủ sở hữu danh hiệu Vua phá lưới ở kỳ World Cup gần nhất với 6 bàn thắng\" width=\"600\" /></p>\r\n\r\n<p>James Rodriguez ĐT Colombia đang l&agrave; cầu thủ sở hữu danh hiệu Vua ph&aacute; lưới ở kỳ World Cup gần nhất với 6 b&agrave;n thắng</p>\r\n\r\n<p><img height=\"846\" sizes=\"(max-width: 600px) 100vw, 600px\" src=\"https://dothethao.net.vn/wp-content/uploads/2018/06/photo-1-1528300410929637434707.jpg\" srcset=\"https://dothethao.net.vn/wp-content/uploads/2018/06/photo-1-1528300410929637434707.jpg 600w, https://dothethao.net.vn/wp-content/uploads/2018/06/photo-1-1528300410929637434707-106x150.jpg 106w, https://dothethao.net.vn/wp-content/uploads/2018/06/photo-1-1528300410929637434707-213x300.jpg 213w\" title=\"Thomas Muller đã có giải đấu thành công và giành danh hiệu Vua phá lưới tại World Cup 2010 ở Nam Phi\" width=\"600\" /></p>\r\n\r\n<p><em>Thomas Muller đ&atilde; c&oacute; giải đấu th&agrave;nh c&ocirc;ng v&agrave; gi&agrave;nh danh hiệu Vua ph&aacute; lưới tại World Cup 2010 ở Nam Phi</em></p>\r\n\r\n<p><img height=\"776\" sizes=\"(max-width: 600px) 100vw, 600px\" src=\"https://dothethao.net.vn/wp-content/uploads/2018/06/photo-2-15283004109431711542855.jpg\" srcset=\"https://dothethao.net.vn/wp-content/uploads/2018/06/photo-2-15283004109431711542855.jpg 600w, https://dothethao.net.vn/wp-content/uploads/2018/06/photo-2-15283004109431711542855-116x150.jpg 116w, https://dothethao.net.vn/wp-content/uploads/2018/06/photo-2-15283004109431711542855-232x300.jpg 232w\" title=\"Miroslav Klose là chủ nhân danh hiệu Giày vàng tại World Cup 2006\" width=\"600\" /></p>\r\n\r\n<p><em>Miroslav Klose l&agrave; chủ nh&acirc;n danh hiệu Gi&agrave;y v&agrave;ng tại World Cup 2006</em></p>\r\n\r\n<p>Trong 11 kỳ World Cup gần nhất, &ldquo;người ngo&agrave;i h&agrave;nh tinh&rdquo; Ronaldo của ĐT Brazil l&agrave; Vua ph&aacute; lưới ghi được nhiều b&agrave;n thắng nhất với 8 pha lập c&ocirc;ng. Khi đ&oacute;, Ro &ldquo;b&eacute;o&rdquo; ẵm danh hiệu n&agrave;y nhờ m&agrave;n tr&igrave;nh diễn m&atilde;n nh&atilde;n tại Nhật Bản v&agrave; H&agrave;n Quốc năm 2002.</p>\r\n\r\n<p><img height=\"779\" sizes=\"(max-width: 600px) 100vw, 600px\" src=\"https://dothethao.net.vn/wp-content/uploads/2018/06/photo-3-1528300410946341001378.jpg\" srcset=\"https://dothethao.net.vn/wp-content/uploads/2018/06/photo-3-1528300410946341001378.jpg 600w, https://dothethao.net.vn/wp-content/uploads/2018/06/photo-3-1528300410946341001378-116x150.jpg 116w, https://dothethao.net.vn/wp-content/uploads/2018/06/photo-3-1528300410946341001378-231x300.jpg 231w\" title=\"Ronaldo khệ nệ \" /></p>\r\n\r\n<p><em>Ronaldo khệ nệ &ldquo;b&ecirc;&rdquo; danh hiệu d&agrave;nh cho Vua ph&aacute; lưới với 8 b&agrave;n ghi được tại World Cup 2002</em></p>\r\n\r\n<p><img height=\"893\" sizes=\"(max-width: 600px) 100vw, 600px\" src=\"https://dothethao.net.vn/wp-content/uploads/2018/06/photo-4-15283004109491452783472.jpg\" srcset=\"https://dothethao.net.vn/wp-content/uploads/2018/06/photo-4-15283004109491452783472.jpg 600w, https://dothethao.net.vn/wp-content/uploads/2018/06/photo-4-15283004109491452783472-101x150.jpg 101w, https://dothethao.net.vn/wp-content/uploads/2018/06/photo-4-15283004109491452783472-202x300.jpg 202w\" title=\"Davor Suker ghi được 6 bàn cho ĐT Croatia và sở hữu danh hiệu Giày vàng tại giải đấu năm 1998\" width=\"600\" /></p>\r\n\r\n<p><em>Davor Suker ghi được 6 b&agrave;n cho ĐT Croatia v&agrave; sở hữu danh hiệu Gi&agrave;y v&agrave;ng tại giải đấu năm 1998</em></p>\r\n\r\n<p>Kỷ lục về số b&agrave;n thắng của một Vua ph&aacute; lưới World Cup vẫn thuộc về Just Fontaine của ĐT Ph&aacute;p với 13 pha l&agrave;m b&agrave;n được thiết lập tại kỳ Mundial năm 1958 tại Thụy Điển. Một điều th&uacute; vị l&agrave; chưa từng c&oacute; cầu thủ n&agrave;o gi&agrave;nh danh hiệu Vua ph&aacute; lưới World Cup ở 2 kỳ li&ecirc;n tiếp.</p>\r\n', '../template4.3.1/images/AnhTinTuc/kienthucsp2.jpg', '2019-12-14', 'Quản trị viên', 2, 1),
(7, 'Bí quyết chọn giày đá bóng cho từng mặt sân ở Việt Nam', '<p>Với những người chơi b&oacute;ng đ&aacute;, đ&ocirc;i gi&agrave;y chiếm vị tr&iacute; rất quan trọng. Ngo&agrave;i kỹ thuật,&nbsp;sự dẻo dai hay những pha đi b&oacute;ng tốc độ th&igrave; đ&ocirc;i gi&agrave;y cũng được coi l&agrave; &ldquo;vũ kh&iacute;&rdquo; v&ocirc; c&ugrave;ng lợi hại. Rất nhiều người chỉ xem đ&oacute; c&oacute; phải một đ&ocirc;i gi&agrave;y chất lượng tốt hay kh&ocirc;ng m&agrave; bỏ qua việc chọn đế gi&agrave;y ph&ugrave; hợp với mặt s&acirc;n m&agrave; m&igrave;nh đ&aacute;.&nbsp;Lựa chọn gi&agrave;y đ&aacute; b&oacute;ng ph&ugrave; hợp với từng mặt s&acirc;n sẽ gi&uacute;p bạn tự tin lướt tr&ecirc;n s&acirc;n b&oacute;ng v&agrave; hạn chế rủi ro, chấn thương.</p>\r\n\r\n<p>Ở Việt Nam c&oacute; 4 loại s&acirc;n: S&acirc;n cỏ nh&acirc;n tạo, s&acirc;n cỏ tự nhi&ecirc;n, s&acirc;n đất v&agrave; s&acirc;n trong nh&agrave;. Trong đ&oacute; chủ yếu l&agrave; s&acirc;n cỏ nh&acirc;n tạo.</p>\r\n\r\n<h3>1. S&acirc;n cỏ nh&acirc;n tạo</h3>\r\n\r\n<p>S&acirc;n cỏ nh&acirc;n tạo c&oacute; lớp cỏ được l&agrave;m bằng nhựa được sản xuất từ sợi tổng hợp v&agrave; những hạt cao su nhỏ phủ tr&ecirc;n lớp cỏ.</p>\r\n\r\n<p>Gi&agrave;y đ&aacute; b&oacute;ng cho s&acirc;n cỏ nh&acirc;n tạo thường sử dụng 2 loại: Gi&agrave;y đinh t&aacute;n (AG) v&agrave; Gi&agrave;y đinh dăm (TF)</p>\r\n\r\n<h4>a) Gi&agrave;y đinh AG&nbsp;(Artificial Grass) &ndash;&nbsp;Gi&agrave;y đinh t&aacute;n</h4>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Đế giày đinh tán\" height=\"287\" sizes=\"(max-width: 600px) 100vw, 600px\" src=\"https://dothethao.net.vn/wp-content/uploads/2018/06/bb3857.jpg\" srcset=\"https://dothethao.net.vn/wp-content/uploads/2018/06/bb3857.jpg 600w, https://dothethao.net.vn/wp-content/uploads/2018/06/bb3857-150x72.jpg 150w, https://dothethao.net.vn/wp-content/uploads/2018/06/bb3857-300x144.jpg 300w\" title=\"Đế giày đinh tán\" width=\"600\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Đế gi&agrave;y đinh t&aacute;n</em></p>\r\n\r\n<p>Đ&acirc;y thực chất mới đ&uacute;ng l&agrave; d&ograve;ng gi&agrave;y để đ&aacute; ở c&aacute;c s&acirc;n cỏ nh&acirc;n tạo, tuy nhi&ecirc;n đấy l&agrave; s&acirc;n đạt chuẩn của c&aacute;c nước ph&aacute;t triển với mặt cỏ bằng, cao su đều. C&ograve;n ở Việt Nam, nếu đ&aacute; kh&ocirc;ng quen tr&ecirc;n c&aacute;c s&acirc;n kh&ocirc;ng chuẩn sẽ chấn thương ngay. Đinh bằng nhựa, thường l&agrave; tr&ograve;n (trừ Adidas c&oacute; một số d&ograve;ng gần đ&acirc;y sử dụng đinh tam gi&aacute;c), cao hơn đinh TF nhưng chưa bằng đinh FG/SG. (Sẽ n&oacute;i ph&iacute;a sau.)</p>\r\n\r\n<blockquote>\r\n<p><strong>Ưu điểm :</strong></p>\r\n\r\n<ul>\r\n	<li>Rất b&aacute;m s&acirc;n (dĩ nhi&ecirc;n)</li>\r\n	<li>Nh&igrave;n rất hổ b&aacute;o, g&acirc;y uy hiếp tinh thần cho đội bạn (kh&agrave; kh&agrave; :D)</li>\r\n	<li>Sức s&aacute;t thương rất cao</li>\r\n</ul>\r\n\r\n<p><strong>Nhược điểm :</strong></p>\r\n\r\n<ul>\r\n	<li>Đối với giầy Nike c&aacute;c loại sẽ kh&ocirc;ng c&oacute; phần đệm bảo vệ g&oacute;t ch&acirc;n.</li>\r\n	<li>Dễ trật sơ mi khi di chuyển kh&ocirc;ng cẩn thận</li>\r\n	<li>Đối với lớp cỏ mỏng, s&acirc;n xấu, cỏ đ&atilde; &ldquo;t&agrave;n hoa bại liễu&rdquo; sẽ c&oacute; cảm gi&aacute;c như đi guốc (nhận x&eacute;t c&aacute; nh&acirc;n của m&igrave;nh)</li>\r\n</ul>\r\n</blockquote>\r\n\r\n<h4>b) Gi&agrave;y đinh TF&nbsp;(Turf Floor)&nbsp;&ndash; Gi&agrave;y đinh dăm</h4>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Đế giày đinh dăm\" height=\"352\" sizes=\"(max-width: 600px) 100vw, 600px\" src=\"https://dothethao.net.vn/wp-content/uploads/2018/06/cach-chon-giay-da-bong-san-co-nhan-tao-dinh-tf-2.jpg\" srcset=\"https://dothethao.net.vn/wp-content/uploads/2018/06/cach-chon-giay-da-bong-san-co-nhan-tao-dinh-tf-2.jpg 600w, https://dothethao.net.vn/wp-content/uploads/2018/06/cach-chon-giay-da-bong-san-co-nhan-tao-dinh-tf-2-150x88.jpg 150w, https://dothethao.net.vn/wp-content/uploads/2018/06/cach-chon-giay-da-bong-san-co-nhan-tao-dinh-tf-2-300x176.jpg 300w\" title=\"Đế giày đinh dăm\" width=\"600\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Đế gi&agrave;y đinh dăm</em></p>\r\n\r\n<p>Thực sự loại gi&agrave;y đ&aacute; b&oacute;ng n&agrave;y thiết kế l&agrave; để tập luyện nhẹ. Tuy nhi&ecirc;n với sự ra đời của nhiều s&acirc;n cỏ nh&acirc;n tạo kh&ocirc;ng thực sự chuẩn, chất lượng cỏ v&agrave; cao su k&eacute;m th&igrave; hầu hết mọi người thường sử dụng loại gi&agrave;y n&agrave;y. Đinh thường c&oacute; dạng như trong h&igrave;nh, bằng nhựa hoặc cao su (Với Adidas v&agrave; Nike ho&agrave;n to&agrave;n 100% cao su) hoặc c&oacute; mặt cả hai vật liệu.</p>\r\n\r\n<blockquote>\r\n<p><strong>Ưu điểm :</strong></p>\r\n\r\n<ul>\r\n	<li>Đế gi&agrave;y c&oacute; l&oacute;t th&ecirc;m 1 phần đệm kh&aacute; mềm, di chuyển sẽ kh&ocirc;ng l&agrave;m bạn đau ch&acirc;n</li>\r\n	<li>Rất b&aacute;m s&acirc;n ngay cả khi trời đang mưa l&acirc;m th&acirc;m (mưa to chắc b&agrave; con k&eacute;o nhau nghỉ đ&aacute; hay đi n&uacute;p rồi, m&igrave;nh kh&ocirc;ng b&agrave;n đến trời gi&ocirc;ng b&atilde;o nh&eacute;!)</li>\r\n</ul>\r\n\r\n<p><strong>Nhược điểm :</strong></p>\r\n\r\n<ul>\r\n	<li>Thực hiện động t&aacute;c kỹ thuật kh&ocirc;ng tốt lắm</li>\r\n	<li>V&agrave;o b&oacute;ng sức s&aacute;t thương b&igrave;nh thường (c&aacute;i n&agrave;y n&ecirc;n để phần ưu điểm hay nhược điểm nhỉ?)</li>\r\n</ul>\r\n</blockquote>\r\n\r\n<h3>2. S&acirc;n cỏ tự nhi&ecirc;n</h3>\r\n\r\n<p>Mấy năm gần đ&acirc;y, số lượng s&acirc;n cỏ tự nhi&ecirc;n ở Việt Nam ng&agrave;y c&agrave;ng &iacute;t. Song, n&oacute; c&oacute; những đặc điểm rất kh&aacute;c so với s&acirc;n cỏ nh&acirc;n tạo. Gi&agrave;y đ&aacute; b&oacute;ng d&agrave;nh cho s&acirc;n cỏ tự nhi&ecirc;n chủ yếu c&oacute; 2 loại: Gi&agrave;y đ&aacute; b&oacute;ng đinh sắt v&agrave; Gi&agrave;y đ&aacute; b&oacute;ng đinh liền.</p>\r\n\r\n<h4>a) Gi&agrave;y đinh SG&nbsp;(Soft Ground) &ndash; Gi&agrave;y đinh sắt</h4>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Đế giày đinh sắt\" height=\"330\" sizes=\"(max-width: 600px) 100vw, 600px\" src=\"https://dothethao.net.vn/wp-content/uploads/2018/06/cach-chon-giay-da-bong-san-co-nhan-tao-dinh-sg-2.jpg\" srcset=\"https://dothethao.net.vn/wp-content/uploads/2018/06/cach-chon-giay-da-bong-san-co-nhan-tao-dinh-sg-2.jpg 600w, https://dothethao.net.vn/wp-content/uploads/2018/06/cach-chon-giay-da-bong-san-co-nhan-tao-dinh-sg-2-150x83.jpg 150w, https://dothethao.net.vn/wp-content/uploads/2018/06/cach-chon-giay-da-bong-san-co-nhan-tao-dinh-sg-2-300x165.jpg 300w\" title=\"Đế giày đinh sắt\" width=\"600\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Đế gi&agrave;y đinh sắt</em></p>\r\n\r\n<p>L&agrave; loại gi&agrave;y đ&aacute; s&acirc;n cỏ tự nhi&ecirc;n c&oacute; nền đất mềm, ẩm, cỏ ngắn (8-10mm) được tưới thường xuy&ecirc;n v&agrave; trước mỗi trận. Đinh thường l&agrave;m bằng sắt hoặc nh&ocirc;m, inox,&hellip; c&oacute; thể th&aacute;o ra bằng cờ l&ecirc;. Đ&acirc;y l&agrave; loại đinh để đ&aacute; ở c&aacute;c s&acirc;n cỏ tự nhi&ecirc;n ti&ecirc;u chuẩn cao ở c&aacute;c nước ph&aacute;t triển của ch&acirc;u &Acirc;u (Anh, Ph&aacute;p, Đức&hellip;), ch&acirc;u Mỹ (Mỹ, Brazil,&hellip;) hay ch&acirc;u &Aacute; (Nhật, H&agrave;n,&hellip;).</p>\r\n\r\n<p>Loại n&agrave;y th&igrave; &iacute;t thấy ở b&oacute;ng đ&aacute; phủi, nhưng c&aacute;c cầu thủ đ&aacute; ở V-League tr&ecirc;n s&acirc;n cỏ chất lượng thấp (như đ&atilde; n&oacute;i ở tr&ecirc;n) vẫn đi, nhất l&agrave; hậu vệ, thủ m&ocirc;n v&igrave; n&oacute; trụ vững, gi&uacute;p b&aacute;m chắc tr&ecirc;n s&acirc;n v&agrave; chặt ch&eacute;m cho sướng (Hehe). Tuy nhi&ecirc;n với những s&acirc;n cỏ chất lượng xấu như ở Việt Nam th&igrave; kh&ocirc;ng n&ecirc;n đ&aacute; loại n&agrave;y, vừa để giữ ch&acirc;n cho m&igrave;nh vừa cho đội bạn bớt hoang mang.</p>\r\n\r\n<p>Như đ&atilde; n&ecirc;u ở tr&ecirc;n, loại gi&agrave;y n&agrave;y KH&Ocirc;NG N&Ecirc;N sử dụng cho cỏ nh&acirc;n tạo v&igrave; dễ g&acirc;y chấn thương cho cả m&igrave;nh v&agrave; đội bạn.</p>\r\n\r\n<h4>b) Gi&agrave;y đinh FG&nbsp;(Firm Ground) &ndash; Gi&agrave;y đinh liền</h4>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Đế giày đinh liền\" height=\"353\" sizes=\"(max-width: 600px) 100vw, 600px\" src=\"https://dothethao.net.vn/wp-content/uploads/2018/06/cach-chon-giay-da-bong-san-co-nhan-tao-dinh-fg-1.jpg\" srcset=\"https://dothethao.net.vn/wp-content/uploads/2018/06/cach-chon-giay-da-bong-san-co-nhan-tao-dinh-fg-1.jpg 600w, https://dothethao.net.vn/wp-content/uploads/2018/06/cach-chon-giay-da-bong-san-co-nhan-tao-dinh-fg-1-150x88.jpg 150w, https://dothethao.net.vn/wp-content/uploads/2018/06/cach-chon-giay-da-bong-san-co-nhan-tao-dinh-fg-1-300x177.jpg 300w\" title=\"Đế giày đinh liền\" width=\"600\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Đế gi&agrave;y đinh liền</em></p>\r\n\r\n<p>L&agrave; loại gi&agrave;y đ&aacute; ở c&aacute;c s&acirc;n cỏ tự nhi&ecirc;n c&oacute; nền đất cứng, cỏ d&agrave;i (10mm đổ l&ecirc;n) kh&ocirc;ng&nbsp;được tưới trước c&aacute;c trận đấu. Đinh thường bằng nhựa đ&uacute;c, gắn liền với đế nhựa. Gần như&nbsp;100% s&acirc;n cỏ tự nhi&ecirc;n ở Việt Nam (H&agrave;ng Đẫy, s&acirc;n tập v&agrave; s&acirc;n ch&iacute;nh Mỹ Đ&igrave;nh, Thống Nhất, Vinh,&hellip;) đều&nbsp;chỉ ở ti&ecirc;u chuẩn n&agrave;y.</p>\r\n\r\n<p>Nhiều bạn vẫn chọn gi&agrave;y n&agrave;y để v&aacute;c đi đ&aacute; cỏ nh&acirc;n tạo ầm ầm, tuy nhi&ecirc;n lời khuy&ecirc;n của&nbsp;<strong>Hồng Ph&uacute;c Sport</strong>&nbsp;l&agrave; KH&Ocirc;NG N&Ecirc;N. Kh&ocirc;ng n&ecirc;n d&ugrave;ng gi&agrave;y n&agrave;y đ&aacute; cỏ nh&acirc;n tạo v&igrave; nếu đi kh&ocirc;ng quen rất dễ g&acirc;y trật mắt c&aacute;, lật cổ ch&acirc;n hoặc bong g&acirc;n. H&atilde;y d&ugrave;ng đ&uacute;ng mục đ&iacute;ch của n&oacute; tr&ecirc;n c&aacute;c s&acirc;n cỏ tự nhi&ecirc;n.</p>\r\n\r\n<h3>3. S&acirc;n trong nh&agrave;</h3>\r\n\r\n<h4>Gi&agrave;y đế IC &ndash; Gi&agrave;y đế bằng</h4>\r\n\r\n<p>Đ&aacute; ở s&acirc;n trong nh&agrave; (Futsal) th&igrave; thường sử dụng đế IC (Indoor Court). Đế bằng cao su, bệt. Ngo&agrave;i ra đế gi&agrave;y kiểu TF cũng l&agrave; một sự lựa chọn th&iacute;ch hợp cho kiểu s&acirc;n thi đấu n&agrave;y.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Đế giày IC\" height=\"322\" sizes=\"(max-width: 600px) 100vw, 600px\" src=\"https://dothethao.net.vn/wp-content/uploads/2018/06/cach-chon-giay-da-bong-san-co-nhan-tao-dinh-futsal-2.jpg\" srcset=\"https://dothethao.net.vn/wp-content/uploads/2018/06/cach-chon-giay-da-bong-san-co-nhan-tao-dinh-futsal-2.jpg 600w, https://dothethao.net.vn/wp-content/uploads/2018/06/cach-chon-giay-da-bong-san-co-nhan-tao-dinh-futsal-2-150x81.jpg 150w, https://dothethao.net.vn/wp-content/uploads/2018/06/cach-chon-giay-da-bong-san-co-nhan-tao-dinh-futsal-2-300x161.jpg 300w\" title=\"Đế giày IC\" width=\"600\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Đế gi&agrave;y IC</em></p>\r\n\r\n<blockquote>\r\n<p><strong>Ưu điểm</strong></p>\r\n\r\n<ul>\r\n	<li>Đ&aacute; được cả ở s&acirc;n cỏ nh&acirc;n tạo v&agrave; Futsal.</li>\r\n	<li>Đ&aacute; b&oacute;ng thực hiện những c&uacute; k&eacute;o gầm, hay di chuyển rất dễ d&agrave;ng, thể hiện kĩ thuật, kĩ năng tốt.</li>\r\n	<li>Gần giống giầy bata bạn hay đi n&ecirc;n khả năng th&iacute;ch ứng tốt. Ph&ugrave; hợp cho bạn n&agrave;o bị trật sơ mi, chấn thương v&igrave; đế thấp dễ di chuyển hơn nhiều nh&eacute;!</li>\r\n</ul>\r\n\r\n<p><strong>Nhược điểm</strong></p>\r\n\r\n<ul>\r\n	<li>Trời mưa sẽ trơn, trượt.</li>\r\n	<li>Kh&ocirc;ng g&acirc;y hoặc g&acirc;y rất &iacute;t nguy hiểm cho đối thủ trong trường hợp tranh chấp b&oacute;ng.</li>\r\n</ul>\r\n</blockquote>\r\n\r\n<h3>4. S&acirc;n đất</h3>\r\n\r\n<p>C&oacute; thể tất cả ch&uacute;ng ta đều đ&atilde; từng đ&aacute; b&oacute;ng ở s&acirc;n đất n&ecirc;n m&igrave;nh sẽ kh&ocirc;ng m&ocirc; tả qu&aacute; nhiều. Mặc d&ugrave; kh&aacute;c nhau về mặt s&acirc;n so với s&acirc;n b&oacute;ng trong nh&agrave;, tuy nhi&ecirc;n, c&aacute;c bạn vẫn c&oacute; thể sử dụng gi&agrave;y đ&aacute; b&oacute;ng đế IC hoặc TF (tương tự như s&acirc;n trong nh&agrave;) nh&eacute;!</p>\r\n\r\n<h3>Kết luận</h3>\r\n\r\n<p>Những lời khuy&ecirc;n ở tr&ecirc;n chỉ l&agrave; tham khảo. Chọn&nbsp;gi&agrave;y đ&aacute; b&oacute;ng n&agrave;o, đế ra sao ho&agrave;n to&agrave;n l&agrave; lựa chọn của bạn. Tuy nhi&ecirc;n nếu chọn gi&agrave;y đ&uacute;ng th&igrave; khả năng chơi b&oacute;ng sẽ kh&ocirc;ng bị hạn chế cũng như giảm thiểu chấn thương hơn rất nhiều. C&aacute;c bạn c&oacute; lời khuy&ecirc;n hoặc &yacute; kiến g&igrave; th&ecirc;m, đừng ngại ngần comment ở dưới! Cũng đừng qu&ecirc;n theo d&otilde;i Website để cập nhật c&aacute;c tin tức thể thao mới nhất nh&eacute;!</p>\r\n', '../template4.3.1/images/AnhTinTuc/kienthucsp3.jpg', '2019-12-22', 'Quản trị viên', 2, 5);

